INSERT INTO `creature_ai_texts` VALUES (-391, 'I know, we thought he was dead too, but that\'s not the case. He\'s trying to use the Sunwell to bring the master of the Burning Legion, Kil\'jaeden, into this world.', NULL, NULL, 'Ich weiÃŸ, wir dachten ebenfalls, er wÃ¤re tot, aber das ist nicht der Fall. Er versucht, den Sonnenbrunnen zu benutzen, um den Herrn der brennenden Legion, Kil\'jaeden, in diese Welt zu bringen.', NULL, NULL, NULL, NULL, NULL, 0, 0, 0, 0, '25162');
