INSERT INTO `creature_text` VALUES (18688, 1, 0, 'The spirits call... I am consumed.', 12, 0, 100, 21, 0, 0, 'Ancient Orc Ancestor');
INSERT INTO `creature_text` VALUES (18688, 1, 1, 'Cannot control... It draws me in... The void grows...', 12, 0, 100, 21, 0, 0, 'Ancient Orc Ancestor');
INSERT INTO `creature_text` VALUES (18688, 1, 2, 'It is dying. It is dying. It is dying.', 12, 0, 100, 21, 0, 0, 'Ancient Orc Ancestor');
