INSERT INTO `creature_text` VALUES (18999, 0, 0, 'Hey, did you read the bounty on this wanted poster?', 12, 7, 100, 6, 0, 0, 'Allerian Defender');
INSERT INTO `creature_text` VALUES (18999, 0, 1, 'The Bone Wastes! I don\'t care what\'s being offered as a reward, I\'m not going into the Bone Wastes!', 12, 7, 100, 6, 0, 0, 'Allerian Defender');
INSERT INTO `creature_text` VALUES (18999, 0, 2, 'This wanted poster says that they\'re offering gold to these so-called adventurers to go kill things. Isn\'t that what we do? I don\'t think I\'m being paid enough!', 12, 7, 100, 6, 0, 0, 'Allerian Defender');
