INSERT INTO `creature_text` VALUES (20787, 0, 0, 'You\'ve saved me, fleshling! Ameer will hear of this noble act.', 12, 0, 100, 0, 0, 0, 'Captain Tyralius');
INSERT INTO `creature_text` VALUES (20904, 0, 0, 'I knew the prince would be angry but, I... I have not been myself. I had to let them out! The great one speaks to me, you see. Wait--outsiders. Kael\'thas did not send you! Good... I\'ll just tell the prince you released the prisoners!', 14, 0, 100, 0, 0, 0, 'Warden Mellichar');
INSERT INTO `creature_text` VALUES (20904, 1, 0, 'The naaru kept some of the most dangerous beings in existence here in these cells. Let me introduce you to another...', 14, 0, 100, 0, 0, 0, 'Warden Mellichar');
INSERT INTO `creature_text` VALUES (20904, 2, 0, 'Yes, yes... another! Your will is mine!', 14, 0, 100, 0, 0, 0, 'Warden Mellichar');
INSERT INTO `creature_text` VALUES (20904, 3, 0, 'Behold another terrifying creature of incomprehensible power!', 14, 0, 100, 0, 0, 0, 'Warden Mellichar');
INSERT INTO `creature_text` VALUES (20904, 4, 0, 'What is this? A lowly gnome? I will do better, O great one.', 14, 0, 100, 0, 0, 0, 'Warden Mellichar');
INSERT INTO `creature_text` VALUES (20904, 5, 0, 'Anarchy! Bedlam! Oh, you are so wise! Yes, I see it now, of course!', 14, 0, 100, 0, 0, 0, 'Warden Mellichar');
INSERT INTO `creature_text` VALUES (20904, 6, 0, 'One final cell remains. Yes, O great one, right away!', 14, 0, 100, 0, 0, 0, 'Warden Mellichar');
INSERT INTO `creature_text` VALUES (20904, 7, 0, 'Welcome, O great one. I am your humble servant.', 14, 0, 100, 0, 0, 0, 'Warden Mellichar');
INSERT INTO `creature_text` VALUES (20977, 0, 0, 'Where in Bonzo\'s brass buttons am I? And who are-- yaaghh, that\'s one mother of a headache!', 12, 0, 100, 0, 0, 0, 'Millhouse Manastorm');
INSERT INTO `creature_text` VALUES (20977, 1, 0, '\"Lowly\"? I don\'t care who you are friend, no one refers to the mighty Millhouse Manastorm as \"Lowly\"! I have no idea what goes on here, but I will gladly join your fight against this impudent imbecile! Prepare to defend yourself, cretin!', 12, 0, 100, 0, 0, 0, 'Millhouse Manastorm');
INSERT INTO `creature_text` VALUES (20977, 2, 0, 'I just need to get some things ready first. You guys go ahead and get started. I need to summon up some water...', 12, 0, 100, 0, 0, 0, 'Millhouse Manastorm');
INSERT INTO `creature_text` VALUES (20977, 3, 0, 'Fantastic! Next, some protective spells. Yes! Now we\'re cookin\'', 12, 0, 100, 0, 0, 0, 'Millhouse Manastorm');
INSERT INTO `creature_text` VALUES (20977, 4, 0, 'And of course I\'ll need some mana. You guys are gonna love this, just wait.', 12, 0, 100, 0, 0, 0, 'Millhouse Manastorm');
INSERT INTO `creature_text` VALUES (20977, 5, 0, 'Aaalllriiiight!! Who ordered up an extra large can of whoop-ass?', 12, 0, 100, 0, 0, 0, 'Millhouse Manastorm');
INSERT INTO `creature_text` VALUES (20977, 6, 0, 'I didn\'t even break a sweat on that one.', 12, 0, 100, 0, 0, 0, 'Millhouse Manastorm');
INSERT INTO `creature_text` VALUES (20977, 6, 1, 'You guys, feel free to jump in anytime.', 12, 0, 100, 0, 0, 0, 'Millhouse Manastorm');
INSERT INTO `creature_text` VALUES (20977, 7, 0, 'I\'m gonna light you up, sweet cheeks!', 12, 0, 100, 0, 0, 0, 'Millhouse Manastorm');
INSERT INTO `creature_text` VALUES (20977, 8, 0, 'Ice, ice, baby!', 12, 0, 100, 0, 0, 0, 'Millhouse Manastorm');
INSERT INTO `creature_text` VALUES (20977, 9, 0, 'Heal me! Oh, for the love of all that is holy, HEAL me! I\'m dying!', 12, 0, 100, 0, 0, 0, 'Millhouse Manastorm');
INSERT INTO `creature_text` VALUES (20977, 10, 0, 'You\'ll be hearing from my lawyer...', 12, 0, 100, 0, 0, 0, 'Millhouse Manastorm');
INSERT INTO `creature_text` VALUES (20977, 11, 0, 'Who\'s bad? Who\'s bad? That\'s right: we bad!', 12, 0, 100, 0, 0, 0, 'Millhouse Manastorm');
