INSERT INTO `creature_text` VALUES (23861, 0, 0, 'The darkness... the corruption... they came too quickly for anyone to know...', 12, 0, 15, 25, 0, 0, 'Restless Apparition');
INSERT INTO `creature_text` VALUES (23861, 0, 1, 'It is too late for us, living one. Take yourself and your friend away from here before you both are... claimed...', 12, 0, 15, 25, 0, 0, 'Restless Apparition');
INSERT INTO `creature_text` VALUES (23861, 0, 2, 'Go away, whoever you are! Witch Hill is mine... mine!', 12, 0, 15, 25, 0, 0, 'Restless Apparition');
INSERT INTO `creature_text` VALUES (23861, 0, 3, 'The darkness will consume all... all the living...', 12, 0, 15, 25, 0, 0, 'Restless Apparition');
