INSERT INTO `creature_text` VALUES (23861, 0, 5, 'Why have you come here, outsider? You will only find pain! Our fate will be yours...', 12, 0, 15, 25, 0, 0, 'Restless Apparition');
INSERT INTO `creature_text` VALUES (23861, 0, 6, 'It was... terrible... the demon...', 12, 0, 15, 25, 0, 0, 'Restless Apparition');
