INSERT INTO `creature_text` VALUES (27316, 2, 0, 'Good. I have your attention, then, Godfrey?', 12, 7, 100, 396, 0, 0, 'Inquisitor Hallard');
INSERT INTO `creature_text` VALUES (27316, 3, 0, 'We can do this in one of two ways, Godfrey. First, I will simply ask you to tell me what the unholy markings etched upon the pages of this tome mean. What say you?', 12, 7, 100, 1, 0, 0, 'Inquisitor Hallard');
INSERT INTO `creature_text` VALUES (27316, 4, 0, 'Then it is option two.', 12, 7, 100, 1, 0, 0, 'Inquisitor Hallard');
INSERT INTO `creature_text` VALUES (27316, 5, 0, 'I say a prayer for you now, lost soul. May the Light take you gracefully.', 12, 7, 100, 68, 4000, 0, 'Inquisitor Hallard');
INSERT INTO `creature_text` VALUES (27316, 6, 0, 'Let the sermon begin.', 12, 7, 100, 1, 0, 0, 'Inquisitor Hallard');
INSERT INTO `creature_text` VALUES (27316, 7, 0, 'LIGHT TAKE YOU, BEAST!', 12, 7, 100, 0, 0, 0, 'Inquisitor Hallard');
INSERT INTO `creature_text` VALUES (27316, 8, 0, 'BURN IN HOLY FIRE!', 12, 7, 100, 0, 0, 0, 'Inquisitor Hallard');
INSERT INTO `creature_text` VALUES (27316, 9, 0, 'LET THE LIGHT IN, GODFREY GOODARD!', 12, 7, 100, 0, 0, 0, 'Inquisitor Hallard');
INSERT INTO `creature_text` VALUES (27316, 10, 0, 'May the power of Light compel you!', 12, 7, 100, 0, 0, 0, 'Inquisitor Hallard');
INSERT INTO `creature_text` VALUES (27316, 11, 0, 'I thought you would see the Light, Godfrey... Now speak quickly, fiend. What does it say?', 12, 7, 100, 396, 0, 0, 'Inquisitor Hallard');
INSERT INTO `creature_text` VALUES (27316, 12, 0, 'RUBBISH! Godfrey do you take me for a fool? Do not spew your Scourge propaganda at a man of the Light, heathen! Speak now or I will have the peasants craft a holy water bath and dip you into it like a dog with fleas!', 12, 7, 100, 6, 0, 0, 'Inquisitor Hallard');
INSERT INTO `creature_text` VALUES (27316, 13, 0, 'What? There is a mausoleum beneath the old village? What fools we are to not have noticed! We will find and strike down your master now, Godfrey. We will end this nightmare.', 12, 7, 100, 6, 0, 0, 'Inquisitor Hallard');
INSERT INTO `creature_text` VALUES (27316, 14, 0, 'You let us worry about how we kill the monster, Godfrey.', 12, 7, 100, 396, 0, 0, 'Inquisitor Hallard');
INSERT INTO `creature_text` VALUES (27316, 15, 0, 'Return to Halford with the information that the good mayor was kind enough to submit, $N. I will finish here and squeeze whatever else this wretch might know about Thel\'zan. Now, I will show Godfrey the rarely seen \"option three.\"', 12, 7, 100, 1, 0, 0, 'Inquisitor Hallard');
INSERT INTO `creature_text` VALUES (27316, 16, 0, 'Let\'s you and I have a chat about some things, Godfrey.', 12, 7, 100, 1, 0, 0, 'Inquisitor Hallard');
INSERT INTO `creature_text` VALUES (27344, 0, 0, 'Guards!', 14, 1, 100, 0, 0, 0, 'Bat Handler Adeline');
INSERT INTO `creature_text` VALUES (27430, 0, 0, 'The Destructive Ward gains in power.', 41, 0, 100, 0, 0, 0, 'Destructive Ward');
INSERT INTO `creature_text` VALUES (27430, 1, 0, 'The Destructive Ward is fully charged!', 41, 0, 100, 0, 0, 0, 'Destructive Ward');
INSERT INTO `creature_text` VALUES (27577, 0, 0, '%s growls.', 16, 0, 100, 0, 0, 409, 'Mayor Godfrey');
INSERT INTO `creature_text` VALUES (27577, 1, 0, '%s hisses!', 16, 0, 100, 0, 0, 410, 'Mayor Godfrey');
INSERT INTO `creature_text` VALUES (27577, 2, 0, 'Tell you nothing, preacher.', 12, 0, 100, 1, 0, 0, 'Mayor Godfrey');
INSERT INTO `creature_text` VALUES (27577, 3, 0, 'What can you do to me that Kel\'Thuzad has not? That the Lich King will not?', 12, 0, 100, 1, 0, 0, 'Mayor Godfrey');
INSERT INTO `creature_text` VALUES (27577, 4, 0, 'The book is your salvation, yes... but nothing will you know! NOTHING I SAY! NOTHING! ', 12, 0, 100, 1, 0, 0, 'Mayor Godfrey');
INSERT INTO `creature_text` VALUES (27577, 5, 0, 'No more LIGHT! NO MORE! I BEG YOU!', 12, 0, 100, 0, 0, 0, 'Mayor Godfrey');
INSERT INTO `creature_text` VALUES (27577, 6, 0, 'Never felt hurt like this!', 12, 0, 100, 0, 0, 0, 'Mayor Godfrey');
INSERT INTO `creature_text` VALUES (27577, 7, 0, 'It tells of the coming apocalypse. How this world will burn and be reborn from death.', 12, 0, 100, 1, 0, 0, 'Mayor Godfrey');
INSERT INTO `creature_text` VALUES (27577, 8, 0, 'NOOOO!!! I tell you! I tell you everything!', 12, 0, 100, 39, 0, 0, 'Mayor Godfrey');
INSERT INTO `creature_text` VALUES (27577, 9, 0, 'Humans... Beneath the earth of Wintergarde Village you built a mausoleum! Why do you think Naxxramas attacked that spot? Thel\'zan hides deep in your own crypt and sends a thousand-thousand corpses at you! Perish you will...', 12, 0, 100, 1, 0, 0, 'Mayor Godfrey');
INSERT INTO `creature_text` VALUES (27577, 10, 0, 'How? Humans truly are stupid, yes? Thel\'zan the Duskbringer! Thel\'zan the Lich! He who is born of human flesh and bone, sacrificed all for power, protected by the Lich King!', 12, 0, 100, 1, 0, 0, 'Mayor Godfrey');
INSERT INTO `creature_text` VALUES (27577, 11, 0, 'You cannot stop Thel\'zan! He bears the dark gift, bestowed upon him by the Lich King himself!', 12, 0, 100, 1, 0, 0, 'Mayor Godfrey');
INSERT INTO `creature_text` VALUES (27656, 0, 0, 'You brash interlopers are out of your element! I will ground you!', 14, 0, 0, 0, 0, 0, 'Boss Eregos - Aggro');
INSERT INTO `creature_text` VALUES (27656, 1, 0, 'Such insolence... such arrogance... must be PUNISHED!', 14, 0, 0, 0, 0, 0, 'Boss Eregos - Enrage');
INSERT INTO `creature_text` VALUES (27656, 2, 0, 'Savor this small victory, foolish little creatures. You and your dragon allies have won this battle. But we will win... the Nexus War.', 14, 0, 0, 0, 0, 0, 'Boss Eregos - Death');
INSERT INTO `creature_text` VALUES (27842, 0, 0, 'Bat gizzards again for the gnomes tonight...', 12, 0, 100, 1, 0, 0, 'Fenrick Barlowe text');
INSERT INTO `creature_text` VALUES (27842, 0, 1, 'What do they expect, making the bats come in at that angle? Broken necks and gamey bat stew, that\'s what they get.', 12, 0, 100, 1, 0, 0, 'Fenrick Barlowe text');
INSERT INTO `creature_text` VALUES (27842, 0, 2, 'We like trees, Fenrick. They provide cover. They won\'t let me chop them down, either.', 12, 0, 100, 1, 0, 0, 'Fenrick Barlowe text');
INSERT INTO `creature_text` VALUES (27842, 0, 3, 'I wonder how many reinforcements need to suffer injury before they allows us to chop down these idiotic trees. They\'re costing us a fortune in bats. Maybe I\'ll rig a harness or two...', 12, 0, 100, 1, 0, 0, 'Fenrick Barlowe text');
INSERT INTO `creature_text` VALUES (27842, 1, 0, 'Arrrhhh...Guards!', 14, 7, 100, 0, 0, 0, 'Common Horde Flight Master');
INSERT INTO `creature_text` VALUES (27915, 0, 0, 'Good work with the crates! Come talk to me in front of Stratholme for your next assignment!', 15, 0, 0, 0, 0, 0, 'Chromie - SAY_EVENT_START');
INSERT INTO `creature_text` VALUES (28012, 0, 0, 'The trickster Mage-Lord Urom protects the third ring. He will appear alone and defenseless, but do not be fooled by appearences! Urom is a powerful conjurer who commands a menagerie of Phantasmal creatures. Seek him out above.', 12, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (28012, 1, 1, 'Your greatest challenge lies ahead. Ley-Guardian Eregos is a Blue dragon of immense power. You will find him flying above the uppermost ring.', 12, 0, 0, 0, 0, 0, '');
INSERT INTO `creature_text` VALUES (28179, 0, 0, 'The Lich King reacted swiftly to the breach. Faster than I anticipated.', 12, 0, 100, 1, 0, 0, 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES (28179, 1, 0, 'What would you have me do, Darion?', 12, 0, 100, 6, 0, 0, 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES (28179, 2, 0, 'Choose your words wisely, death knight. You stand amidst the company of the devoted.', 12, 0, 100, 1, 0, 0, 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES (28179, 3, 0, 'We will do this with honor, Darion. We will not sink to the levels of the Scourge to be victorious. To do so would make us no better than the monster that we fight to destroy!', 12, 0, 100, 1, 0, 0, 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES (28518, 0, 0, 'Not sure how ol\' Nass always finds his way back here. Must be by smell....', 12, 0, 100, 0, 0, 0, 'Stefan Vadu');
INSERT INTO `creature_text` VALUES (28521, 0, 0, 'Let\'s go to Zul\'Drak he says. We can get to the bottom of this mess, he says...', 12, 0, 33, 0, 0, 0, 'Nass');
INSERT INTO `creature_text` VALUES (28521, 0, 1, 'One more flock of scourge hair gained, one more shred of dignity lost...', 12, 0, 33, 0, 0, 0, 'Nass');
INSERT INTO `creature_text` VALUES (28521, 0, 2, 'Are we finished yet?', 12, 0, 33, 0, 0, 0, 'Nass');
INSERT INTO `creature_text` VALUES (28532, 0, 0, 'Indeed. Watch this, $r.', 12, 0, 100, 1, 0, 0, 'Bloodrose Datura');
INSERT INTO `creature_text` VALUES (28532, 1, 0, 'Here, troll... a gift!', 12, 0, 100, 0, 0, 0, 'Bloodrose Datura');
INSERT INTO `creature_text` VALUES (28532, 2, 0, 'Ugh... disgusting!', 12, 0, 100, 0, 0, 0, 'Bloodrose Datura');
INSERT INTO `creature_text` VALUES (28541, 0, 0, 'For me? Really, mon?', 12, 0, 100, 6, 0, 0, 'Captured Drakkari Scout');
INSERT INTO `creature_text` VALUES (28541, 1, 0, 'It....it be beautiful!', 12, 0, 100, 5, 0, 0, 'Captured Drakkari Scout');
INSERT INTO `creature_text` VALUES (28546, 0, 0, 'You wish to confront the master? You must first weather the storm!', 14, 0, 100, 0, 0, 14453, 'Ionar');
INSERT INTO `creature_text` VALUES (28546, 1, 0, 'The slightest spark shall be your undoing.', 14, 0, 100, 0, 0, 14454, 'Ionar');
INSERT INTO `creature_text` VALUES (28546, 1, 1, 'No one is safe!', 14, 0, 100, 0, 0, 14455, 'Ionar');
INSERT INTO `creature_text` VALUES (28546, 2, 0, 'Shocking ... I know!', 14, 0, 100, 0, 0, 14456, 'Ionar');
INSERT INTO `creature_text` VALUES (28546, 2, 1, 'You atempt the unpossible.', 14, 0, 100, 0, 0, 14457, 'Ionar');
INSERT INTO `creature_text` VALUES (28546, 2, 2, 'Your spark of light is ... extinguish.', 14, 0, 100, 0, 0, 14458, 'Ionar');
INSERT INTO `creature_text` VALUES (28546, 3, 0, 'Master... you have guests.', 14, 0, 100, 0, 0, 14459, 'Ionar');
INSERT INTO `creature_text` VALUES (28563, 0, 0, 'The structure above begins to hum with energy.', 42, 0, 0, 0, 0, 0, 'Freya\'s Presence - On Call of the Lifewarden cast');
INSERT INTO `creature_text` VALUES (28566, 0, 0, 'Beginning the distillation in 5 seconds.', 12, 0, 100, 0, 0, 0, 'Tipsy McManus');
INSERT INTO `creature_text` VALUES (28566, 1, 0, 'Add another orange, quickly!', 12, 0, 100, 25, 0, 0, 'Tipsy McManus');
INSERT INTO `creature_text` VALUES (28566, 2, 0, 'Put a papaya in the still!', 12, 0, 100, 25, 0, 0, 'Tipsy McManus');
INSERT INTO `creature_text` VALUES (28566, 3, 0, 'Add bananas!', 12, 0, 100, 25, 0, 0, 'Tipsy McManus');
INSERT INTO `creature_text` VALUES (28566, 4, 0, 'Pressure\'s too high! Open the pressure valve!', 12, 0, 100, 5, 0, 0, 'Tipsy McManus');
INSERT INTO `creature_text` VALUES (28566, 5, 0, 'The still needs heat! Light the brazier!', 12, 0, 100, 5, 0, 0, 'Tipsy McManus');
INSERT INTO `creature_text` VALUES (28566, 6, 0, 'Good job! Keep your eyes open, now.', 12, 0, 100, 4, 0, 0, 'Tipsy McManus');
INSERT INTO `creature_text` VALUES (28566, 6, 1, 'That\'ll do. Never know what it\'ll need next...', 12, 0, 100, 4, 0, 0, 'Tipsy McManus');
INSERT INTO `creature_text` VALUES (28566, 6, 2, 'Nicely handled! Stay on your toes!', 12, 0, 100, 4, 0, 0, 'Tipsy McManus');
INSERT INTO `creature_text` VALUES (28566, 6, 3, 'Well done! Be ready for anything!', 12, 0, 100, 4, 0, 0, 'Tipsy McManus');
INSERT INTO `creature_text` VALUES (28566, 7, 0, 'It\'s no good! I\'m shutting it down...', 12, 0, 100, 0, 0, 0, 'Tipsy McManus');
INSERT INTO `creature_text` VALUES (28566, 8, 0, 'We\'ve done it! Come get the cask.', 12, 0, 100, 0, 0, 0, 'Tipsy McManus');
INSERT INTO `creature_text` VALUES (28586, 0, 0, 'I am the greatest of my father\'s sons! Your end has come!', 14, 0, 100, 0, 0, 14149, 'General Bjarngrim');
INSERT INTO `creature_text` VALUES (28586, 1, 0, 'Give me your worst!', 14, 0, 100, 0, 0, 14150, 'General Bjarngrim');
INSERT INTO `creature_text` VALUES (28586, 2, 0, 'Defend yourself, for all the good it will do!', 14, 0, 100, 0, 0, 14151, 'General Bjarngrim');
INSERT INTO `creature_text` VALUES (28586, 3, 0, 'GRAAAAAH! Behold the fury of iron and steel!', 14, 0, 100, 0, 0, 14152, 'General Bjarngrim');
INSERT INTO `creature_text` VALUES (28586, 4, 0, 'So ends your curse!', 14, 0, 100, 0, 0, 14153, 'General Bjarngrim');
INSERT INTO `creature_text` VALUES (28586, 4, 1, 'Flesh... is... weak!', 14, 0, 100, 0, 0, 14154, 'General Bjarngrim');
INSERT INTO `creature_text` VALUES (28586, 4, 2, '...', 14, 0, 100, 0, 0, 14155, 'General Bjarngrim');
INSERT INTO `creature_text` VALUES (28586, 5, 0, 'How can it be...? Flesh is not... stronger!', 14, 0, 100, 0, 0, 14156, 'General Bjarngrim');
INSERT INTO `creature_text` VALUES (28586, 6, 0, '%s switches to Defensive Stance!', 41, 0, 100, 0, 0, 0, 'General Bjarngrim');
INSERT INTO `creature_text` VALUES (28586, 7, 0, '%s switches to Battle Stance!', 41, 0, 100, 0, 0, 0, 'General Bjarngrim');
INSERT INTO `creature_text` VALUES (28586, 8, 0, '%s switches to Berserker Stance!', 41, 0, 100, 0, 0, 0, 'General Bjarngrim');
INSERT INTO `creature_text` VALUES (28587, 0, 0, 'It is you who have destroyed my children? You... shall... pay!', 14, 0, 100, 0, 0, 13960, 'Volkhan');
INSERT INTO `creature_text` VALUES (28587, 1, 0, 'Life from lifelessness... death for you.', 14, 0, 100, 0, 0, 13961, 'Volkhan');
INSERT INTO `creature_text` VALUES (28587, 1, 1, 'Nothing is wasted in the process. You will see....', 14, 0, 100, 0, 0, 13962, 'Volkhan');
INSERT INTO `creature_text` VALUES (28587, 2, 0, 'I will crush you beneath my boots!', 14, 0, 100, 0, 0, 13963, 'Volkhan');
INSERT INTO `creature_text` VALUES (28587, 2, 1, 'All my work... undone!', 14, 0, 100, 0, 0, 13964, 'Volkhan');
INSERT INTO `creature_text` VALUES (28587, 3, 0, 'The armies of iron will conquer all!', 14, 0, 100, 0, 0, 13965, 'Volkhan');
INSERT INTO `creature_text` VALUES (28587, 3, 1, 'Ha, pathetic!', 14, 0, 100, 0, 0, 13966, 'Volkhan');
INSERT INTO `creature_text` VALUES (28587, 3, 2, 'You have cost me too much work!', 14, 0, 100, 0, 0, 13967, 'Volkhan');
INSERT INTO `creature_text` VALUES (28587, 4, 0, 'The master was right... to be concerned.', 14, 0, 100, 0, 0, 13968, 'Volkhan');
INSERT INTO `creature_text` VALUES (28587, 5, 0, '%s runs to his anvil!', 41, 0, 100, 0, 0, 0, 'Volkhan');
INSERT INTO `creature_text` VALUES (28587, 6, 0, '%s prepares to shatter his Brittle Golems!', 41, 0, 100, 0, 0, 0, 'Volkhan');
INSERT INTO `creature_text` VALUES (28601, 0, 0, 'Fool! You led us to the only being that could stand up to our armies! You will never bring the Etymidian back to Northrend!', 12, 0, 100, 25, 0, 0, 'High Cultist Herenn');
INSERT INTO `creature_text` VALUES (28859, 0, 0, 'My patience has reached its limit, I will be rid of you!', 14, 0, 0, 0, 0, 14517, 'Malygos - Aggro (Phase 1)');
INSERT INTO `creature_text` VALUES (28859, 1, 0, 'Your stupidity has finally caught up to you', 14, 0, 0, 0, 0, 14519, 'Malygos - Killed Player (1) (Phase 1)');
INSERT INTO `creature_text` VALUES (28859, 1, 1, 'More artifacts to confiscate...', 14, 0, 0, 0, 0, 14520, 'Malygos - Killed Player (2) (Phase 1)');
INSERT INTO `creature_text` VALUES (28859, 1, 2, '<Laughs> How very... naive...', 14, 0, 0, 0, 0, 14521, 'Malygos - Killed Player (3) (Phase 1)');
INSERT INTO `creature_text` VALUES (28859, 2, 0, 'I had hoped to end your lives quickly, but you have proven more... resilient then I had anticipated. Nonetheless, your efforts are in vain, it is you reckless, careless mortals who are to blame for this war! I do what I must... And if it means your... extinction... THEN SO BE IT', 14, 0, 0, 0, 0, 14522, 'Malygos - End Phase One');
INSERT INTO `creature_text` VALUES (28859, 3, 0, 'Few have experienced the pain I will now inflict upon you!', 14, 0, 0, 0, 0, 14523, 'Malygos - Aggro (Phase 2)');
INSERT INTO `creature_text` VALUES (28859, 4, 0, 'I will teach you IGNORANT children just how little you know of magic...', 14, 0, 0, 0, 0, 14524, 'Malygos - Anti-Magic Shell');
INSERT INTO `creature_text` VALUES (28859, 5, 0, 'Watch helplessly as your hopes are swept away...', 14, 0, 0, 0, 0, 14525, 'Malygos - Magic Blast');
INSERT INTO `creature_text` VALUES (28859, 6, 0, 'Your energy will be put to good use!', 14, 0, 0, 0, 0, 14526, 'Malygos - Killed Player 1 (Phase 2)');
INSERT INTO `creature_text` VALUES (28859, 6, 1, 'I am the spell-weaver! My power is infinite!', 14, 0, 0, 0, 0, 14527, 'Malygos - Killed Player 2 (Phase 2)');
INSERT INTO `creature_text` VALUES (28859, 6, 2, 'Your spirit will linger here forever!', 14, 0, 0, 0, 0, 14528, 'Malygos - Killed Player 3 (Phase 2)');
INSERT INTO `creature_text` VALUES (28859, 7, 0, 'ENOUGH! If you intend to reclaim Azeroth\'s magic, then you shall have it...', 14, 0, 0, 0, 0, 14529, 'Malygos - End Phase 2');
INSERT INTO `creature_text` VALUES (28859, 8, 0, 'Now your benefactors make their appearance... But they are too late. The powers contained here are sufficient to destroy the world ten times over! What do you think they will do to you?', 14, 0, 0, 0, 0, 14530, 'Intro Phase 3');
INSERT INTO `creature_text` VALUES (28859, 9, 0, 'SUBMIT!', 14, 0, 0, 0, 0, 14531, 'Malygos - Aggro (Phase 3)');
INSERT INTO `creature_text` VALUES (28859, 10, 0, 'The powers at work here exceed anything you could possibly imagine!', 14, 0, 0, 0, 0, 14532, 'Malygos - Surge of Power');
INSERT INTO `creature_text` VALUES (28859, 11, 0, 'I AM UNSTOPPABLE!', 14, 0, 0, 0, 0, 14533, 'Malygos - Buffed by a spark');
INSERT INTO `creature_text` VALUES (28859, 12, 0, 'Alexstrasza! Another of your brood falls!', 14, 0, 0, 0, 0, 14534, 'Malygos - Killed Player 1 (Phase 3)');
INSERT INTO `creature_text` VALUES (28859, 12, 1, 'Little more then gnats!', 14, 0, 0, 0, 0, 14535, 'Malygos - Killed Player 2 (Phase 3)');
INSERT INTO `creature_text` VALUES (28859, 12, 2, 'Your red allies will share your fate...', 14, 0, 0, 0, 0, 14536, 'Malygos - Killed Player 3 (Phase 3)');
INSERT INTO `creature_text` VALUES (28859, 13, 0, 'Still standing? Not for long...', 14, 0, 0, 0, 0, 14537, 'Malygos - Spell Casting 1(Phase 3)');
INSERT INTO `creature_text` VALUES (28859, 13, 1, 'Your cause is lost', 14, 0, 0, 0, 0, 14538, 'Malygos - Spell Casting 2 (Phase 3)');
INSERT INTO `creature_text` VALUES (28859, 13, 2, 'Your fragile mind will be shattered!', 14, 0, 0, 0, 0, 14539, 'Malygos - Spell Casting 3 (Phase 3)');
INSERT INTO `creature_text` VALUES (28859, 14, 0, 'Unthinkable! The mortals will destroy... everything... my sister... what have you...', 14, 0, 0, 0, 0, 0, 'Malygos - Death');
INSERT INTO `creature_text` VALUES (28914, 0, 0, 'Make haste, $N. Use the portal!', 12, 0, 100, 25, 0, 0, 'Orbaz Bloodlane');
INSERT INTO `creature_text` VALUES (28923, 0, 0, 'I have witnessed the rise and fall of empires... the birth and extinction of entire species... Over countless millennia the foolishness of mortals has remained the only constant. Your presence here confirms this.', 14, 0, 100, 0, 0, 14160, 'Loken');
INSERT INTO `creature_text` VALUES (28923, 1, 0, 'My master has shown me the future, and you have no place in it. Azeroth will be reborn in darkness. Yogg-Saron shall be released! The Pantheon shall fall!', 14, 0, 100, 0, 0, 14161, 'Loken');
INSERT INTO `creature_text` VALUES (28923, 2, 0, 'What hope is there for you? None!', 14, 0, 100, 0, 0, 14162, 'Loken');
INSERT INTO `creature_text` VALUES (28923, 3, 0, 'You cannot hide from fate!', 14, 0, 100, 0, 0, 14163, 'Loken');
INSERT INTO `creature_text` VALUES (28923, 3, 1, 'Come closer. I will make it quick.', 14, 0, 100, 0, 0, 14164, 'Loken');
INSERT INTO `creature_text` VALUES (28923, 3, 2, 'Your flesh cannot hold out for long.', 14, 0, 100, 0, 0, 14165, 'Loken');
INSERT INTO `creature_text` VALUES (28923, 4, 0, 'Only mortal...', 14, 0, 100, 0, 0, 14166, 'Loken');
INSERT INTO `creature_text` VALUES (28923, 4, 1, 'I... am... FOREVER!', 14, 0, 100, 0, 0, 14167, 'Loken');
INSERT INTO `creature_text` VALUES (28923, 4, 2, 'What little time you had, you wasted!', 14, 0, 100, 0, 0, 14168, 'Loken');
INSERT INTO `creature_text` VALUES (28923, 5, 0, 'You stare blindly into the abyss!', 14, 0, 100, 0, 0, 14169, 'Loken');
INSERT INTO `creature_text` VALUES (28923, 6, 0, 'Your ignorance is profound. Can you not see where this path leads?', 14, 0, 100, 0, 0, 14170, 'Loken');
INSERT INTO `creature_text` VALUES (28923, 7, 0, 'You cross the precipice of oblivion!', 14, 0, 100, 0, 0, 14171, 'Loken');
INSERT INTO `creature_text` VALUES (28923, 8, 0, 'My death... heralds the end of this world.', 14, 0, 100, 0, 0, 14172, 'Loken');
INSERT INTO `creature_text` VALUES (28923, 9, 0, '%s begins to cast Lightning Nova!', 41, 0, 100, 0, 0, 0, 'Loken');
INSERT INTO `creature_text` VALUES (29280, 0, 0, 'Your sacrifice is a testament to your obedience. Indeed you are worthy of this charge. Arise, and forever be known as Svala Sorrowgrave!', 14, 0, 0, 0, 0, 14732, 'Image of Arthas SAY_DIALOG_OF_ARTHAS_1');
INSERT INTO `creature_text` VALUES (29280, 1, 0, 'Your first test awaits you. Destroy our uninvited guests.', 14, 0, 0, 0, 0, 14733, 'Image of Arthas SAY_DIALOG_OF_ARTHAS_2');
INSERT INTO `creature_text` VALUES (29281, 0, 0, 'My liege! I have done as you asked, and now beseech you for your blessing!', 14, 0, 0, 0, 0, 13856, 'Svala SAY_DIALOG_WITH_ARTHAS_1');
INSERT INTO `creature_text` VALUES (29327, 0, 0, 'It\'s an angry male!', 42, 0, 100, 0, 0, 0, 'Male Frost Leopard');
INSERT INTO `creature_text` VALUES (29466, 0, 0, 'I can\'t believe it! I\'m free to go!', 12, 0, 100, 5, 0, 0, 'Goblin Prisoner');
INSERT INTO `creature_text` VALUES (29466, 0, 1, 'I\'m free? I\'m free!', 12, 0, 100, 5, 0, 0, 'Goblin Prisoner');
INSERT INTO `creature_text` VALUES (29466, 0, 2, 'Time to hightail it! Thanks, friend!', 12, 0, 100, 5, 0, 0, 'Goblin Prisoner');
INSERT INTO `creature_text` VALUES (29618, 0, 0, 'Grrrrr!', 12, 0, 100, 0, 0, 0, 'Snowblind Follower');
INSERT INTO `creature_text` VALUES (29618, 0, 1, 'Me not afraid!', 12, 0, 100, 0, 0, 0, 'Snowblind Follower');
INSERT INTO `creature_text` VALUES (29618, 0, 2, 'Net not stop me! No... net stop me.', 12, 0, 100, 0, 0, 0, 'Snowblind Follower');
INSERT INTO `creature_text` VALUES (29618, 0, 3, 'No kill me!', 12, 0, 100, 0, 0, 0, 'Snowblind Follower');
INSERT INTO `creature_text` VALUES (29618, 0, 4, 'No!', 12, 0, 100, 0, 0, 0, 'Snowblind Follower');
INSERT INTO `creature_text` VALUES (29618, 0, 5, 'You no take... me!', 12, 0, 100, 0, 0, 0, 'Snowblind Follower');
INSERT INTO `creature_text` VALUES (29762, 0, 0, 'Guards!', 14, 1, 100, 0, 0, 0, 'Hyeyoung Parka');
INSERT INTO `creature_text` VALUES (30146, 0, 0, 'Curse you! You will not treat me like a beast!', 12, 0, 100, 1, 0, 0, 'Exhausted Vrykul');
INSERT INTO `creature_text` VALUES (30146, 0, 1, 'Enough! I will teach you some manners, wench!', 12, 0, 100, 1, 0, 0, 'Exhausted Vrykul');
INSERT INTO `creature_text` VALUES (30146, 0, 2, 'I\'d rather die fighting than live like a slave!', 12, 0, 100, 1, 0, 0, 'Exhausted Vrykul');
INSERT INTO `creature_text` VALUES (30146, 1, 0, 'Back... to work...', 12, 0, 100, 1, 0, 0, 'Exhausted Vrykul');
INSERT INTO `creature_text` VALUES (30146, 1, 1, 'We will have revenge... some day.', 12, 0, 100, 1, 0, 0, 'Exhausted Vrykul');
INSERT INTO `creature_text` VALUES (30146, 1, 2, 'You treat us worse than animals!', 12, 0, 100, 1, 0, 0, 'Exhausted Vrykul');
INSERT INTO `creature_text` VALUES (30190, 0, 0, 'Metallic specimen of foreign origin detected.', 12, 0, 100, 0, 5, 0, 'Attendant Tock');
INSERT INTO `creature_text` VALUES (30190, 1, 0, 'Beginning analysis...', 12, 0, 100, 0, 0, 0, 'Attendant Tock');
INSERT INTO `creature_text` VALUES (30190, 2, 0, 'Specimen identified as an alloy of saronite and iron.', 12, 0, 100, 0, 0, 0, 'Attendant Tock');
INSERT INTO `creature_text` VALUES (30190, 3, 0, 'The ore used in this sample originated deep wihin the crust of Azeroth and was recently unearthed.', 12, 0, 100, 275, 0, 0, 'Attendant Tock');
INSERT INTO `creature_text` VALUES (30190, 4, 0, 'The saronite in this sample is nearly pure. Ores like this do not occur naturally on the surface of Azeroth.', 12, 0, 100, 0, 0, 0, 'Attendant Tock');
INSERT INTO `creature_text` VALUES (30190, 5, 0, 'This ore can only have been created by a powerful, malevolent force. Attempting to access databanks for more information...', 12, 0, 100, 1, 0, 0, 'Attendant Tock');
INSERT INTO `creature_text` VALUES (30190, 6, 0, 'Access denied?!', 12, 0, 100, 6, 0, 0, 'Attendant Tock');
INSERT INTO `creature_text` VALUES (30190, 7, 0, 'Further information can only be accessed from the archives within Ulduar, by order of Keeper Loken.', 12, 0, 100, 1, 0, 0, 'Attendant Tock');
INSERT INTO `creature_text` VALUES (30190, 8, 0, 'But one thing is for certain: this plate\'s size, thickness, and bolt holes point to a use as armor for a colossal structure.', 12, 0, 100, 1, 0, 0, 'Attendant Tock');
INSERT INTO `creature_text` VALUES (30190, 9, 0, 'Directive completed. Returning TO standby mode.', 12, 0, 100, 1, 0, 0, 'Attendant Tock');
INSERT INTO `creature_text` VALUES (30271, 0, 0, 'Guards!', 14, 1, 100, 0, 0, 0, 'Galendror Whitewing');
INSERT INTO `creature_text` VALUES (30377, 0, 0, 'You are dealing with a being that holds within it the consciousness of the most cunning, intelligent, and ruthless individuals to ever live.', 12, 0, 100, 1, 0, 0, 'The Ebon Watcher');
INSERT INTO `creature_text` VALUES (30377, 1, 0, 'The Lich King is unlike any foe that you have ever faced, Highlord. Though you bested him upon the holy ground of Light\'s Hope Chapel, you tread now upon his domain.', 12, 0, 100, 1, 0, 0, 'The Ebon Watcher');
INSERT INTO `creature_text` VALUES (30377, 2, 0, 'You cannot win. Not like this...', 12, 0, 100, 274, 0, 0, 'The Ebon Watcher');
INSERT INTO `creature_text` VALUES (30377, 3, 0, 'Nothing. There is nothing that you can do while the Light binds you. It controls you wholly, shackling you to the ground with its virtues.', 12, 0, 100, 274, 0, 0, 'The Ebon Watcher');
INSERT INTO `creature_text` VALUES (30377, 4, 0, '%s shakes his head.', 16, 0, 100, 0, 0, 0, 'The Ebon Watcher');
INSERT INTO `creature_text` VALUES (30377, 5, 0, 'Look upon the field, Highlord. The Lich King has halted your advance completely and won the upper hand!', 12, 0, 100, 25, 0, 0, 'The Ebon Watcher');
INSERT INTO `creature_text` VALUES (30377, 6, 0, 'The breach you created was sealed with Nerubian webbing almost as quickly as it was opened.', 12, 0, 100, 1, 0, 0, 'The Ebon Watcher');
INSERT INTO `creature_text` VALUES (30377, 7, 0, 'Your soldiers are being used as living shields to stave off artillery fire in the Valley of Echoes, allowing the forces of the Lich King to assault your base without impediment.', 12, 0, 100, 1, 0, 0, 'The Ebon Watcher');
INSERT INTO `creature_text` VALUES (30377, 8, 0, 'The Lich King knows your boundaries, Highlord. He knows that you will not fire on your own men. Do you not understand? He has no boundaries. No rules to abide.', 12, 0, 100, 1, 0, 0, 'The Ebon Watcher');
INSERT INTO `creature_text` VALUES (30377, 9, 0, 'Then you have lost, Highlord.', 12, 0, 100, 1, 0, 0, 'The Ebon Watcher');
INSERT INTO `creature_text` VALUES (31237, 0, 0, 'You shouldn\'t have done that!  He\'s not going to like it.', 15, 0, 100, 0, 0, 0, 'Matthias Lehner - On quest accept');
INSERT INTO `creature_text` VALUES (31397, 0, 0, 'AHAHAHAHA... you\'ll join us soon enough!', 14, 0, 100, 0, 0, 0, 'Saronite Mine Slave');
INSERT INTO `creature_text` VALUES (31397, 0, 1, 'I don\'t want to leave! I want to stay here!', 14, 0, 100, 0, 0, 0, 'Saronite Mine Slave');
INSERT INTO `creature_text` VALUES (31397, 0, 2, 'NO! You\'re wrong! The voices in my head are beautiful!', 14, 0, 100, 0, 0, 0, 'Saronite Mine Slave');
INSERT INTO `creature_text` VALUES (31397, 0, 3, 'My life for you!', 14, 0, 100, 0, 0, 0, 'Saronite Mine Slave');
INSERT INTO `creature_text` VALUES (31397, 0, 4, 'I\'m coming, master!', 14, 0, 100, 0, 0, 0, 'Saronite Mine Slave');
INSERT INTO `creature_text` VALUES (31426, 0, 0, 'Guards!', 14, 1, 100, 0, 0, 0, 'Doras');
INSERT INTO `creature_text` VALUES (32295, 0, 0, 'I did what I had to, brother. You gave me no alternative.', 14, 0, 0, 0, 0, 0, 'Alexstrasza - Yell One');
INSERT INTO `creature_text` VALUES (32295, 1, 0, 'And so ends the Nexus War.', 14, 0, 0, 0, 0, 0, 'Alexstrasza - Yell Two');
INSERT INTO `creature_text` VALUES (32295, 2, 0, 'This resolution pains me deeply, but the destruction, the monumental loss of life had to end. Regardless of Malygos\' recent transgressions, I will mourn his loss. He was once a guardian, a protector. This day, one of the world\'s mightiest has fallen.', 14, 0, 0, 0, 0, 0, 'Alexstrasza - Yell Three');
INSERT INTO `creature_text` VALUES (32295, 3, 0, 'The red dragonflight will take on the burden of mending the devastation wrought on Azeroth. Return home to your people and rest. Tomorrow will bring you new challenges, and you must be ready to face them. Life... goes on.', 14, 0, 0, 0, 0, 0, 'Alexstrasza - Yell Four');
INSERT INTO `creature_text` VALUES (33008, 0, 0, 'It\'s an angry male!', 42, 0, 100, 0, 0, 0, 'Male Icepaw Bear');
INSERT INTO `creature_text` VALUES (33788, 0, 0, 'Help shield us from these cutting winds, little sapling.', 12, 7, 100, 2, 0, 0, 'Fhyron Shadesong');
INSERT INTO `creature_text` VALUES (33788, 0, 1, 'There you are', 12, 7, 100, 273, 0, 0, 'Fhyron Shadesong');
INSERT INTO `creature_text` VALUES (33788, 0, 2, 'Grow, little one.', 12, 7, 100, 273, 0, 0, 'Fhyron Shadesong');
INSERT INTO `creature_text` VALUES (33972, 0, 0, 'Use Shield-Breaker on a Charge Target$B|TInterface\\Icons\\ability_warrior_shieldbreak.blp:32|t$BFollow up with Charge while the target is vulnerable$B|TInterface\\Icons\\ability_mount_charger.blp:32|t', 42, 0, 0, 0, 0, 0, 'Argent Tournament - Charge Tutorial');
INSERT INTO `creature_text` VALUES (33973, 0, 0, 'Put up defend$B|TInterface\\Icons\\ability_warrior_shieldmastery.blp:32|t$BThen use Thrust on a Melee Target$B|TInterface\\Icons\\inv_sword_65.blp:32|t', 42, 0, 0, 0, 0, 0, 'Argent Tournament - Melee Tutorial');
INSERT INTO `creature_text` VALUES (33974, 0, 0, 'Use Shield-Breaker on a Ranged Target$B|TInterface\\Icons\\ability_warrior_shieldbreak.blp:32|t$BThen use Shield-Breaker while the target is defenseless$B|TInterface\\Icons\\ability_warrior_shieldbreak.blp:32|t', 42, 0, 0, 0, 0, 0, 'Argent Tournament - Ranged Tutorial');
INSERT INTO `creature_text` VALUES (34716, 0, 0, 'Thank you, $c.', 12, 0, 100, 5, 0, 0, 'Captive Aspirant');
INSERT INTO `creature_text` VALUES (34716, 0, 1, 'They\'re killing the prisoners! Hurry, help the others.', 12, 0, 100, 5, 0, 0, 'Captive Aspirant');
INSERT INTO `creature_text` VALUES (34716, 0, 2, 'I knew I shouldn\'t have come to the tournament!', 12, 0, 100, 5, 0, 0, 'Captive Aspirant');
INSERT INTO `creature_text` VALUES (34716, 0, 3, 'I thought I was going to die in there!', 12, 0, 100, 5, 0, 0, 'Captive Aspirant');
INSERT INTO `creature_text` VALUES (34723, 0, 0, 'Dat cloud look like da Warchief.', 12, 1, 100, 25, 0, 0, 'Watcher Tolwe - Random');
INSERT INTO `creature_text` VALUES (34723, 0, 1, 'Dere be a bug.', 12, 1, 100, 25, 0, 0, 'Watcher Tolwe - Random');
INSERT INTO `creature_text` VALUES (34956, 0, 0, 'They were seconds from strapping me to one of those altars and sucking my soul out. Let\'s get out of here!', 12, 0, 100, 5, 0, 0, 'Kul the Reckless');
INSERT INTO `creature_text` VALUES (35642, 0, 0, 'If you\'ll excuse me, I\'m afraid I have other business I must attend to. Please call on me again in the future.', 12, 0, 100, 3, 0, 0, 'Jeeves');
INSERT INTO `creature_text` VALUES (36597, 0, 0, 'So the Light\'s vaunted justice has finally arrived? Shall I lay down Frostmourne and throw myself at your mercy, Fordring?', 14, 0, 0, 0, 0, 17349, 'The Lich King - SAY_LK_INTRO_1');
INSERT INTO `creature_text` VALUES (36597, 1, 0, 'You\'ll learn of that first hand. When my work is complete, you will beg for mercy -- and I will deny you. Your anguished cries will be testament to my unbridled power...', 14, 0, 0, 22, 0, 17350, 'The Lich King - SAY_LK_INTRO_2');
INSERT INTO `creature_text` VALUES (36597, 2, 0, 'I\'ll keep you alive to witness the end, Fordring. I would not want the Light\'s greatest champion to miss seeing this wretched world remade in my image.', 14, 0, 0, 0, 0, 17351, 'The Lich King - SAY_LK_INTRO_3');
INSERT INTO `creature_text` VALUES (36597, 3, 0, 'Come then champions, feed me your rage!', 14, 0, 0, 0, 0, 0, 'The Lich King - SAY_LK_AGGRO');
INSERT INTO `creature_text` VALUES (36597, 4, 0, 'I will freeze you from within until all that remains is an icy husk!', 14, 0, 0, 0, 0, 17369, 'The Lich King - SAY_LK_REMORSELESS_WINTER');
INSERT INTO `creature_text` VALUES (36597, 5, 0, 'Watch as the world around you collapses!', 14, 0, 0, 0, 0, 17370, 'The Lich King - SAY_LK_QUAKE');
INSERT INTO `creature_text` VALUES (36597, 6, 0, 'Val\'kyr, your master calls!', 14, 0, 0, 0, 0, 17373, 'The Lich King - SAY_LK_SUMMON_VALKYR');
INSERT INTO `creature_text` VALUES (36597, 7, 0, 'Frostmourne hungers...', 14, 0, 0, 0, 0, 17366, 'The Lich King - SAY_LK_HARVEST_SOUL');
INSERT INTO `creature_text` VALUES (36597, 8, 0, 'Argh... Frostmourne, obey me!', 14, 0, 0, 0, 0, 17367, 'The Lich King - SAY_LK_FROSTMOURNE_ESCAPE');
INSERT INTO `creature_text` VALUES (36597, 9, 0, 'Frostmourne feeds on the soul of your fallen ally!', 14, 0, 0, 0, 0, 17368, 'The Lich King - SAY_LK_FROSTMOURNE_KILL');
INSERT INTO `creature_text` VALUES (36597, 10, 0, 'Hope wanes!', 14, 0, 0, 0, 0, 17363, 'The Lich King - SAY_LK_KILL');
INSERT INTO `creature_text` VALUES (36597, 10, 1, 'The end has come!', 14, 0, 0, 0, 0, 17364, 'The Lich King - SAY_LK_KILL');
INSERT INTO `creature_text` VALUES (36597, 11, 0, 'Face now your tragic end!', 14, 0, 0, 0, 0, 17365, 'The Lich King - SAY_LK_BERSERK');
INSERT INTO `creature_text` VALUES (36597, 12, 0, '%s begins to cast Defile!', 41, 0, 0, 0, 0, 0, 'The Lich King - EMOTE_DEFILE_WARNING');
INSERT INTO `creature_text` VALUES (36597, 13, 0, '|TInterface\\Icons\\ability_creature_disease_02.blp:16|tYou have been infected by |cFFCF00FFNecrotic Plague!|r', 42, 0, 0, 0, 0, 0, 'The Lich King - EMOTE_NECROTIC_PLAGUE_WARNING');
INSERT INTO `creature_text` VALUES (36597, 14, 0, 'No questions remain unanswered. No doubts linger. You ARE Azeroth\'s greatest champions. You overcame every challenge I laid before you. My mightiest servants have fallen before your relentless onslaught... your unbridled fury...', 14, 0, 0, 0, 0, 17353, 'The Lich King - SAY_LK_OUTRO_1');
INSERT INTO `creature_text` VALUES (36597, 15, 0, 'Is it truly the righteousness that drives you? I wonder...', 14, 0, 0, 0, 0, 17354, 'The Lich King - SAY_LK_OUTRO_2');
INSERT INTO `creature_text` VALUES (36597, 16, 0, 'You trained them well, Fordring. You delivered the greatest fighting force this world has ever known... right into my hands -- exactly as I intended. You shall be rewarded for your unwitting sacrifice.', 14, 0, 0, 0, 0, 17355, 'The Lich King - SAY_LK_OUTRO_3');
INSERT INTO `creature_text` VALUES (36597, 17, 0, 'Watch now as I raise them from the dead to become masters of the Scourge. They will shroud this world in chaos and destruction. Azeroth\'s fall will come at their hands -- and you will be the first to die.', 14, 0, 0, 0, 0, 17356, 'The Lich King - SAY_LK_OUTRO_4');
INSERT INTO `creature_text` VALUES (36597, 18, 0, 'I delight in the irony...', 14, 0, 0, 0, 0, 17357, 'The Lich King - SAY_LK_OUTRO_5');
INSERT INTO `creature_text` VALUES (36597, 19, 0, 'Impossible...', 14, 0, 0, 0, 0, 17358, 'The Lich King - SAY_LK_OUTRO_6');
INSERT INTO `creature_text` VALUES (36597, 20, 0, 'Now I stand, the lion before the lambs... and they do not fear.', 14, 0, 0, 0, 0, 17361, 'The Lich King - SAY_LK_OUTRO_7');
INSERT INTO `creature_text` VALUES (36597, 21, 0, 'They cannot fear.', 14, 0, 0, 0, 0, 17362, 'The Lich King - SAY_LK_OUTRO_8');
INSERT INTO `creature_text` VALUES (36627, 4, 0, '|TInterface\\Icons\\spell_shadow_unstableaffliction_2.blp:16|t%s begins to cast |cFFFF0000Unstable Ooze Explosion!|r', 41, 0, 0, 0, 0, 0, 'Rotface - EMOTE_UNSTABLE_EXPLOSION');
INSERT INTO `creature_text` VALUES (36627, 9, 0, '|TInterface\\Icons\\ability_creature_disease_02.blp:16|tYou have |cFF00FF00Mutated Infection!|r', 42, 0, 0, 0, 0, 0, 'Rotface - EMOTE_MUTATED_INFECTION');
INSERT INTO `creature_text` VALUES (36678, 9, 0, '|TInterface\\Icons\\inv_misc_herb_evergreenmoss.blp:16|t%s cast |cFF00FF00Malleable Goo!|r', 41, 0, 0, 0, 0, 0, 'Professor Putricide - EMOTE_MALLEABLE_GOO');
INSERT INTO `creature_text` VALUES (36774, 0, 0, 'You Sunreaver lackeys are all the same.', 12, 0, 100, 1, 0, 0, 'Silver Covenant Agent');
INSERT INTO `creature_text` VALUES (36774, 0, 1, 'You won\'t escape from us so easily!', 12, 0, 100, 1, 0, 0, 'Silver Covenant Agent');
INSERT INTO `creature_text` VALUES (36776, 0, 0, 'Did you think you would escape with your life?', 12, 0, 100, 1, 0, 0, 'Sunreaver Agent');
INSERT INTO `creature_text` VALUES (36776, 0, 1, 'You\'ll never run us out of the city, Silver Covenant thug!', 12, 0, 100, 1, 0, 0, 'Sunreaver Agent');
INSERT INTO `creature_text` VALUES (36789, 0, 0, 'Heroes, lend me your aid. I... I cannot hold them off much longer. You must heal my wounds!', 14, 0, 0, 0, 0, 17064, 'Valithria Dreamwalker - SAY_VALITHRIA_ENTER_COMBAT');
INSERT INTO `creature_text` VALUES (36789, 1, 0, 'I have opened a portal into the Dream. Your salvation lies within, heroes...', 14, 0, 0, 0, 0, 17068, 'Valithria Dreamwalker - SAY_VALITHRIA_DREAM_PORTAL');
INSERT INTO `creature_text` VALUES (36789, 2, 0, 'My strength is returning! Press on, heroes!', 14, 0, 0, 0, 0, 17070, 'Valithria Dreamwalker - SAY_VALITHRIA_75_PERCENT');
INSERT INTO `creature_text` VALUES (36789, 3, 0, 'I will not last much longer!', 14, 0, 0, 0, 0, 17069, 'Valithria Dreamwalker - SAY_VALITHRIA_25_PERCENT');
INSERT INTO `creature_text` VALUES (36789, 4, 0, 'Forgive me for what I do! I... cannot... stop... ONLY NIGHTMARES REMAIN!', 14, 0, 0, 0, 0, 17072, 'Valithria Dreamwalker - SAY_VALITHRIA_DEATH');
INSERT INTO `creature_text` VALUES (36789, 5, 0, 'A tragic loss...', 14, 0, 0, 0, 0, 17066, 'Valithria Dreamwalker - SAY_VALITHRIA_PLAYER_DEATH');
INSERT INTO `creature_text` VALUES (36789, 6, 0, 'FAILURES!', 14, 0, 0, 0, 0, 17067, 'Valithria Dreamwalker - SAY_VALITHRIA_BERSERK');
INSERT INTO `creature_text` VALUES (36789, 7, 0, 'I am renewed! Ysera grants me the favor to lay these foul creatures to rest!', 14, 0, 0, 0, 0, 17071, 'Valithria Dreamwalker - SAY_VALITHRIA_SUCCESS');
INSERT INTO `creature_text` VALUES (36823, 0, 0, 'You have come to bring Arthas to justice? To see the Lich King destroyed?', 14, 0, 0, 0, 0, 17394, 'Terenas Menethil - SAY_TERENAS_INTRO_1');
INSERT INTO `creature_text` VALUES (36823, 1, 0, 'First, you must escape Frostmourne\'s hold, or be damned as I am; trapped within this cursed blade for all eternity.', 14, 0, 0, 0, 0, 17395, 'Terenas Menethil - SAY_TERENAS_INTRO_2');
INSERT INTO `creature_text` VALUES (36823, 2, 0, 'Aid me in destroying these tortured souls! Together we will loosen Frostmourne\'s hold and weaken the Lich King from within!', 14, 0, 0, 0, 0, 17396, 'Terenas Menethil - SAY_TERENAS_INTRO_3');
INSERT INTO `creature_text` VALUES (36853, 0, 0, 'You are fools to have come to this place! The icy winds of Northrend will consume your souls!', 14, 0, 0, 0, 0, 17007, 'Sindragosa - SAY_AGGRO');
INSERT INTO `creature_text` VALUES (36853, 1, 0, 'Suffer, mortals, as your pathetic magic betrays you!', 14, 0, 0, 0, 0, 17014, 'Sindragosa - SAY_UNCHAINED_MAGIC');
INSERT INTO `creature_text` VALUES (36853, 2, 0, '%s prepares to unleash a wave of blistering cold!', 41, 0, 0, 0, 0, 0, 'Sindragosa - EMOTE_WARN_BLISTERING_COLD');
INSERT INTO `creature_text` VALUES (36853, 3, 0, 'Can you feel the cold hand of death upon your heart?', 14, 0, 0, 0, 0, 17013, 'Sindragosa - SAY_BLISTERING_COLD');
INSERT INTO `creature_text` VALUES (36853, 4, 0, 'Aaah! It burns! What sorcery is this?!', 14, 0, 0, 0, 0, 17015, 'Sindragosa - SAY_RESPITE_FOR_A_TORMENTED_SOUL');
INSERT INTO `creature_text` VALUES (36853, 5, 0, 'Your incursion ends here! None shall survive!', 14, 0, 0, 0, 0, 17012, 'Sindragosa - SAY_AIR_PHASE');
INSERT INTO `creature_text` VALUES (36853, 6, 0, 'Now feel my master\'s limitless power and despair!', 14, 0, 0, 0, 0, 17016, 'Sindragosa - SAY_PHASE_2');
INSERT INTO `creature_text` VALUES (36853, 7, 0, '%s fires a frozen orb towards $N!', 41, 0, 0, 0, 0, 0, 'Sindragosa - EMOTE_WARN_FROZEN_ORB');
INSERT INTO `creature_text` VALUES (36853, 8, 0, 'Perish!', 14, 0, 0, 0, 0, 17008, 'Sindragosa - SAY_KILL 1');
INSERT INTO `creature_text` VALUES (36853, 8, 1, 'A flaw of mortality...', 14, 0, 0, 0, 0, 17009, 'Sindragosa - SAY_KILL 2');
INSERT INTO `creature_text` VALUES (36853, 9, 0, 'Enough! I tire of these games!', 14, 0, 0, 0, 0, 17011, 'Sindragosa - SAY_BERSERK');
INSERT INTO `creature_text` VALUES (36853, 10, 0, 'Free...at last...', 14, 0, 0, 0, 0, 17010, 'Sindragosa - SAY_DEATH');
INSERT INTO `creature_text` VALUES (37007, 0, 0, 'The master\'s sanctum has been disturbed!', 14, 0, 0, 0, 0, 16865, 'Deathbound Ward - SAY_TRAP_ACTIVATE');
INSERT INTO `creature_text` VALUES (37007, 0, 1, 'I... awaken!', 14, 0, 0, 0, 0, 16866, 'Deathbound Ward - SAY_TRAP_ACTIVATE');
INSERT INTO `creature_text` VALUES (37007, 0, 2, 'Who... goes there...?', 14, 0, 0, 0, 0, 16867, 'Deathbound Ward - SAY_TRAP_ACTIVATE');
INSERT INTO `creature_text` VALUES (37119, 0, 0, 'This is our final stand. What happens here will echo through the ages. Regardless of outcome, they will know that we fought with honor. That we fought for the freedom and safety of our people!', 14, 0, 0, 22, 0, 16653, 'Highlord Tirion Fordring - SAY_TIRION_INTRO_1');
INSERT INTO `creature_text` VALUES (37119, 1, 0, 'Remember, heroes, fear is your greatest enemy in these befouled halls. Steel your heart and your soul will shine brighter than a thousand suns. The enemy will falter at the sight of you. They will fall as the light of righteousness envelops them!', 14, 0, 0, 22, 0, 16654, 'Highlord Tirion Fordring - SAY_TIRION_INTRO_2');
INSERT INTO `creature_text` VALUES (37119, 2, 0, 'Our march upon Icecrown Citadel begins now!', 14, 0, 0, 22, 0, 16655, 'Highlord Tirion Fordring - SAY_TIRION_INTRO_3');
INSERT INTO `creature_text` VALUES (37119, 3, 0, 'ARTHAS! I swore that I would see you dead and the Scourge dismantled! I\'m going to finish what I started at Light\'s Hope!', 14, 0, 0, 22, 0, 16656, 'Highlord Tirion Fordring - SAY_TIRION_INTRO_4');
INSERT INTO `creature_text` VALUES (37119, 4, 0, 'The power of the Light knows no bounds, Saurfang. His soul is under great strain, but he lives - for now.', 12, 0, 0, 1, 0, 16658, 'Highlord Tirion Fordring - SAY_TIRION_INTRO_5');
INSERT INTO `creature_text` VALUES (37119, 5, 0, 'By the Light, it must be so!', 12, 0, 0, 5, 0, 16657, 'Highlord Tirion Fordring - SAY_TIRION_INTRO_A_5');
INSERT INTO `creature_text` VALUES (37122, 0, 0, 'Never... could reach... the top shelf...', 14, 0, 0, 0, 0, 16586, 'Captain Arnath - SAY_ARNATH_DEATH');
INSERT INTO `creature_text` VALUES (37122, 1, 0, 'You miserable fools never did manage to select a decent bat wing.', 14, 0, 0, 0, 0, 16587, 'Captain Arnath - SAY_ARNATH_RESURRECTED');
INSERT INTO `creature_text` VALUES (37122, 2, 0, 'THAT was for bringing me spoiled spider ichor!', 14, 0, 0, 0, 0, 16588, 'Captain Arnath - SAY_ARNATH_KILL');
INSERT INTO `creature_text` VALUES (37122, 3, 0, 'Don\'t... let Finklestein use me... for his potions...', 14, 0, 0, 0, 0, 16589, 'Captain Arnath - SAY_ARNATH_SECOND_DEATH');
INSERT INTO `creature_text` VALUES (37122, 4, 0, 'The loss of our comrades was unpreventable. They lived and died in the service of the Argent Crusade.', 14, 0, 0, 0, 0, 16590, 'Captain Arnath - SAY_ARNATH_SURVIVE_TALK');
INSERT INTO `creature_text` VALUES (37122, 5, 0, 'Even dying here beats spending another day collecting reagents for that madman, Finklestein.', 14, 0, 0, 0, 0, 16585, 'Captain Arnath - SAY_ARNATH_INTRO_2');
INSERT INTO `creature_text` VALUES (37123, 0, 0, 'No amount of healing can save me now. Fight on, brothers...', 14, 0, 0, 0, 0, 16810, 'Captain Brandon - SAY_BRANDON_DEATH');
INSERT INTO `creature_text` VALUES (37123, 1, 0, 'What? This strength...? All of the pain is gone! You... must join me in the eternal embrace of death!', 14, 0, 0, 0, 0, 16811, 'Captain Brandon - SAY_BRANDON_RESURRECTED');
INSERT INTO `creature_text` VALUES (37123, 2, 0, 'It doesn\'t hurt anymore, does it?', 14, 0, 0, 0, 0, 16812, 'Captain Brandon - SAY_BRANDON_KILL');
INSERT INTO `creature_text` VALUES (37123, 3, 0, 'I\'m sorry...', 14, 0, 0, 0, 0, 16813, 'Captain Brandon - SAY_BRANDON_SECOND_DEATH');
INSERT INTO `creature_text` VALUES (37123, 4, 0, 'You have done much in this war against the Scourge. May the light embrace you.', 14, 0, 0, 0, 0, 16815, 'Captain Brandon - SAY_BRANDON_SURVIVE_TALK');
INSERT INTO `creature_text` VALUES (37124, 0, 0, 'Please... burn my remains. Let me live warm in the afterlife...', 14, 0, 0, 0, 0, 16844, 'Captain Grondel - SAY_GRONDEL_DEATH');
INSERT INTO `creature_text` VALUES (37124, 1, 0, 'No! Why was I denied a death by flame? You must all BURN!', 14, 0, 0, 0, 0, 16845, 'Captain Grondel - SAY_GRONDEL_RESURRECTED');
INSERT INTO `creature_text` VALUES (37124, 2, 0, 'Can you feel the burn?', 14, 0, 0, 0, 0, 16846, 'Captain Grondel - SAY_GRONDEL_KILL');
INSERT INTO `creature_text` VALUES (37124, 3, 0, 'What... have I done? No!', 14, 0, 0, 0, 0, 16847, 'Captain Grondel - SAY_GRONDEL_SECOND_DEATH');
INSERT INTO `creature_text` VALUES (37124, 4, 0, 'What can possibly redeem this unholy place? Thank you...', 14, 0, 0, 0, 0, 16849, 'Captain Grondel - SAY_GRONDEL_SURVIVE_TALK');
INSERT INTO `creature_text` VALUES (37125, 0, 0, 'It was... a worthy afterlife.', 14, 0, 0, 0, 0, 16998, 'Captain Rupert - SAY_RUPERT_DEATH');
INSERT INTO `creature_text` VALUES (37125, 1, 0, 'There is no escaping the Lich King\'s will. Prepare for an explosive encounter!', 14, 0, 0, 0, 0, 16999, 'Captain Rupert - SAY_RUPERT_RESURRECTED');
INSERT INTO `creature_text` VALUES (37125, 2, 0, 'So that\'s what happens when you stand too close to a bomb!', 14, 0, 0, 0, 0, 17000, 'Captain Rupert - SAY_RUPERT_KILL');
INSERT INTO `creature_text` VALUES (37125, 3, 0, 'What an... explosive ending!', 14, 0, 0, 0, 0, 17001, 'Captain Rupert - SAY_RUPERT_SECOND_DEATH');
INSERT INTO `creature_text` VALUES (37125, 4, 0, 'Beware the dangers that lie ahead... and do try to remain in one piece.', 14, 0, 0, 0, 0, 17003, 'Captain Rupert - SAY_RUPERT_SURVIVE_TALK');
INSERT INTO `creature_text` VALUES (37126, 0, 0, 'You may have once fought beside me, Crok, but now you are nothing more than a traitor. Come, your second death approaches!', 14, 0, 0, 0, 0, 17017, 'Sister Svalna - SAY_EVENT_START');
INSERT INTO `creature_text` VALUES (37126, 1, 0, 'Miserable creatures! Die!', 14, 0, 0, 0, 0, 17018, 'Sister Svalna - SAY_KILL_CAPTAIN');
INSERT INTO `creature_text` VALUES (37126, 2, 0, 'Foolish Crok. You brought my reinforcements with you. Arise, Argent Champions, and serve the Lich King in death!', 14, 0, 0, 0, 0, 17019, 'Sister Svanlna - SAY_RESURRECT_CAPTAINS');
INSERT INTO `creature_text` VALUES (37126, 3, 0, 'Come, Scourgebane. I\'ll show the master which of us is truly worthy of the title of \"Champion\"!', 14, 0, 0, 0, 0, 17020, 'Sister Svalna - SAY_AGGRO');
INSERT INTO `creature_text` VALUES (37126, 4, 0, 'What a pitiful choice of an ally, Crok!', 14, 0, 0, 0, 0, 17021, 'Sister Svalna - SAY_KILL');
INSERT INTO `creature_text` VALUES (37126, 5, 0, 'What? They died so easily? No matter.', 14, 0, 0, 0, 0, 17022, 'Sister Svalna - SAY_CAPTAIN_DEATH');
INSERT INTO `creature_text` VALUES (37126, 6, 0, 'Perhaps... you were right, Crok.', 14, 0, 0, 0, 0, 17023, 'Sister Svalna - SAY_DEATH');
INSERT INTO `creature_text` VALUES (37126, 7, 0, '%s has impaled $N!', 41, 0, 0, 0, 0, 0, 'Sister Svalna - EMOTE_SVALNA_IMPALE');
INSERT INTO `creature_text` VALUES (37126, 8, 0, '%s\'s Aether Shield has been shattered by $N!', 41, 0, 0, 0, 0, 0, 'Sister Svalna - EMOTE_SVALNA_BROKEN_SHIELD');
INSERT INTO `creature_text` VALUES (37129, 0, 0, 'Ready your arms, my Argent Brothers. The Vrykul will protect the Frost Queen with their lives.', 14, 0, 0, 0, 0, 16819, 'Crok Scourgebane - SAY_CROK_INTRO_1');
INSERT INTO `creature_text` VALUES (37129, 1, 0, 'Enough idle banter! Our champions have arrived - support them as we push our way through the hall!', 14, 0, 0, 0, 0, 16820, 'Crok Scourgebane - SAY_CROK_INTRO_3');
INSERT INTO `creature_text` VALUES (37129, 2, 0, 'Draw them back to us, and we\'ll assist you.', 14, 0, 0, 0, 0, 16821, 'Crok Scourgebane - SAY_CROK_COMBAT_WP_0');
INSERT INTO `creature_text` VALUES (37129, 3, 0, 'Quickly, push on!', 14, 0, 0, 0, 0, 16823, 'Crok Scourgebane - SAY_CROK_COMBAT_WP_1');
INSERT INTO `creature_text` VALUES (37129, 4, 0, 'Her reinforcements will arrive shortly, we must bring her down quickly!', 14, 0, 0, 0, 0, 16824, 'Crok Scourgebane - SAY_CROK_FINAL_WP');
INSERT INTO `creature_text` VALUES (37129, 5, 0, 'I\'ll draw her attacks. Return our brothers to their graves, then help me bring her down!', 14, 0, 0, 15, 0, 16825, 'Crok Scourgebane - SAY_CROK_COMBAT_SVALNA');
INSERT INTO `creature_text` VALUES (37129, 6, 0, 'I must rest for a moment', 14, 0, 0, 0, 0, 16826, 'Crok Scourgebane - SAY_CROK_WEAKENING_GAUNTLET');
INSERT INTO `creature_text` VALUES (37129, 7, 0, 'Champions, I cannot hold her back any longer!', 14, 0, 0, 0, 0, 16827, 'Crok Scourgebane - SAY_CROK_WEAKENING_SVALNA');
INSERT INTO `creature_text` VALUES (37129, 8, 0, 'Vengeance alone... was not enough!', 14, 0, 0, 0, 0, 16828, 'Crok Scourgebane - SAY_CROK_DEATH');
INSERT INTO `creature_text` VALUES (37181, 0, 0, 'You now stand upon the hallowed ground of the Scourge. The Light won\'t protect you here, paladin. Nothing will protect you...', 14, 0, 0, 0, 0, 17230, 'The Lich King - SAY_LK_INTRO_1');
INSERT INTO `creature_text` VALUES (37181, 1, 0, 'You could have been my greatest champion, Fordring: A force of darkness that would wash over this world and deliver it into a new age of strife.', 14, 0, 0, 0, 0, 17231, 'The Lich King - SAY_LK_INTRO_2');
INSERT INTO `creature_text` VALUES (37181, 2, 0, 'But that honor is no longer yours. Soon, I will have a new champion.', 14, 0, 0, 0, 0, 17232, 'The Lich King - SAY_LK_INTRO_3');
INSERT INTO `creature_text` VALUES (37181, 3, 0, 'The breaking of this one has been taxing. The atrocities I have committed upon his soul. He has resisted for so long, but he will bow down before his king soon.', 14, 0, 0, 0, 0, 17233, 'The Lich King - SAY_LK_INTRO_4');
INSERT INTO `creature_text` VALUES (37181, 4, 0, 'In the end, you will all serve me.', 14, 0, 0, 0, 0, 17234, 'The Lich King - SAY_LK_INTRO_5');
INSERT INTO `creature_text` VALUES (37183, 0, 0, 'NEVER! I... I will never... serve... you...', 14, 0, 0, 0, 0, 17078, 'Highlord Bolvar Fordragon - SAY_BOLVAR_INTRO_1');
INSERT INTO `creature_text` VALUES (37187, 15, 0, 'The paladin still lives? Is it possible, Highlord? Could he have survived?', 12, 0, 0, 6, 0, 17107, 'High Overlord Saurfang - SAY_SAURFANG_INTRO_1');
INSERT INTO `creature_text` VALUES (37187, 16, 0, 'Then we must save him! If we rescue Bolvar Fordragon, we may quell the unrest between the Alliance and the Horde.', 12, 0, 0, 5, 0, 17108, 'High Overlord Saurfang - SAY_SAURFANG_INTRO_2');
INSERT INTO `creature_text` VALUES (37187, 17, 0, 'Our mission is now clear: The Lich King will answer for his crimes and we will save Highlord Bolvar Fordragon!', 12, 0, 0, 15, 0, 17109, 'High Overlord Saurfang - SAY_SAURFANG_INTRO_3');
INSERT INTO `creature_text` VALUES (37187, 18, 0, 'Kor\'kron, prepare Orgrim\'s Hammer for its final voyage! Champions, our gunship will find a point to dock on the upper reaches of the citadel. Meet us there!', 14, 0, 0, 22, 0, 17110, 'High Overlord Saurfang - SAY_SAURFANG_INTRO_4');
INSERT INTO `creature_text` VALUES (37200, 13, 0, 'Could it be, Lord Fordring? If Bolvar lives, mayhap there is hope fer peace between the Alliance and the Horde. We must reach the top o\' this cursed place and free the paladin!', 12, 0, 0, 6, 0, 16980, 'Muradin Bronzebeard - SAY_SAURFANG_INTRO_2');
INSERT INTO `creature_text` VALUES (37200, 14, 0, 'Prepare the Skybreaker fer an aerial assault on the citadel!', 14, 0, 0, 5, 0, 16981, 'Muradin Bronzebeard - SAY_SAURFANG_INTRO_3');
INSERT INTO `creature_text` VALUES (37200, 15, 0, 'Heroes, ye must fight yer way to a clear extraction point within Icecrown. We\'ll try an\' rendezvous on the ramparts!', 14, 0, 0, 22, 0, 16982, 'Muradin Bronzebeard - SAY_SAURFANG_INTRO_4');
INSERT INTO `creature_text` VALUES (37217, 0, 0, '%s cries out with a loud, baying howl!', 41, 0, 0, 0, 0, 0, 'Precious - EMOTE_PRECIOUS_ZOMBIES');
INSERT INTO `creature_text` VALUES (37491, 1, 0, 'You miserable fools never did manage to select a decent bat wing.', 14, 0, 0, 0, 0, 16587, 'Captain Arnath - SAY_ARNATH_RESURRECTED');
INSERT INTO `creature_text` VALUES (37491, 2, 0, 'THAT was for bringing me spoiled spider ichor!', 14, 0, 0, 0, 0, 16588, 'Captain Arnath - SAY_ARNATH_KILL');
INSERT INTO `creature_text` VALUES (37491, 3, 0, 'Don\'t... let Finklestein use me... for his potions...', 14, 0, 0, 0, 0, 16589, 'Captain Arnath - SAY_ARNATH_SECOND_DEATH');
