INSERT INTO `creature_text` VALUES (38579, 0, 0, 'Free at last! It is over, my son. This is the moment of reckoning.', 14, 0, 0, 1, 0, 17397, 'Terenas Menethil - SAY_TERENAS_OUTRO_1');
INSERT INTO `creature_text` VALUES (38579, 1, 0, 'Rise up, champions of the Light!', 14, 0, 0, 0, 0, 17398, 'Terenas Menethil - SAY_TERENAS_OUTRO_2');
INSERT INTO `creature_text` VALUES (38995, 0, 0, 'We\'ll grant you a swift death, Arthas. More than can be said for the thousands you\'ve tortured and slain.', 14, 0, 0, 0, 0, 17390, 'Highlord Tirion Fordring - SAY_TIRION_INTRO_1');
INSERT INTO `creature_text` VALUES (38995, 1, 0, 'So be it. Champions, attack!', 14, 0, 0, 0, 0, 17391, 'Highlord Tirion Fordring - SAY_TIRION_INTRO_2');
INSERT INTO `creature_text` VALUES (38995, 2, 0, 'LIGHT, GRANT ME ONE FINAL BLESSING. GIVE ME THE STRENGTH... TO SHATTER THESE BONDS!', 14, 0, 0, 0, 0, 17392, 'Highlord Tirion Fordring - SAY_TIRION_OUTRO_1');
INSERT INTO `creature_text` VALUES (38995, 3, 0, 'No more, Arthas! No more lives will be consumed by your hatred!', 14, 0, 0, 0, 0, 17393, 'Highlord Tirion Fordring - SAY_TIRION_OUTRO_2');
INSERT INTO `creature_text` VALUES (39217, 0, 0, 'You have come to bring Arthas to justice? To see the Lich King destroyed?', 14, 0, 0, 0, 0, 17394, 'Terenas Menethil - SAY_TERENAS_INTRO_1');
INSERT INTO `creature_text` VALUES (39217, 1, 0, 'First, you must escape Frostmourne\'s hold, or be damned as I am; trapped within this cursed blade for all eternity.', 14, 0, 0, 0, 0, 17395, 'Terenas Menethil - SAY_TERENAS_INTRO_2');
INSERT INTO `creature_text` VALUES (39746, 0, 0, 'Alexstrasza has chosen capable allies.... A pity that I must END YOU!', 14, 0, 100, 0, 0, 17512, 'Baltharus the Warborn');
INSERT INTO `creature_text` VALUES (39746, 1, 0, 'You thought you stood a chance?', 14, 0, 50, 0, 0, 17513, 'General Zarithrian');
INSERT INTO `creature_text` VALUES (39746, 1, 1, 'It\'s for the best.', 14, 0, 50, 0, 0, 17514, 'General Zarithrian');
INSERT INTO `creature_text` VALUES (39746, 2, 0, 'Turn them to ash, minions!', 14, 0, 100, 0, 0, 17516, 'General Zarithrian');
