INSERT INTO `creature_text` VALUES (29308, 3, 1, 'I will drink no blood before it\'s time.', 14, 0, 100, 0, 0, 14366, 'prince taldaram SAY_SLAY_1');
INSERT INTO `creature_text` VALUES (29308, 3, 2, 'One final embrace.', 14, 0, 100, 0, 0, 14367, 'prince taldaram SAY_SLAY_2');
INSERT INTO `creature_text` VALUES (29308, 4, 0, 'Still I hunger. Still... I... thirst.', 14, 0, 100, 0, 0, 14368, 'prince taldaram SAY_DEATH');
INSERT INTO `creature_text` VALUES (29308, 5, 0, 'So... appetizing.', 14, 0, 100, 0, 0, 14363, 'prince taldaram SAY_FEED1');
INSERT INTO `creature_text` VALUES (29308, 5, 1, 'Fresh, warm blood. It has been too long.', 14, 0, 100, 0, 0, 14364, 'prince taldaram SAY_FEED2');
INSERT INTO `creature_text` VALUES (29308, 6, 0, 'Your heartbeat is... music to my ears.', 14, 0, 100, 0, 0, 14361, 'prince taldaram SAY_VANISH1');
INSERT INTO `creature_text` VALUES (29308, 6, 1, 'I am nowhere... I am everywhere. I am the watcher, unseen.', 14, 0, 100, 0, 0, 14362, 'prince taldaram SAY_VANISH2');
INSERT INTO `creature_text` VALUES (34064, 0, 0, 'We did it, lads! We got here before Algalon\'s arrival. Maybe we can rig the systems to interfere with his analysis--', 14, 0, 0, 0, 0, 15824, 'Brann Bronzebeard - SAY_BRANN_ALGALON_INTRO_1');
INSERT INTO `creature_text` VALUES (34064, 1, 0, 'I\'ll head back to the Archivum and see if I can jam his signal. I might be able to buy us some time while you take care of him.', 12, 0, 0, 0, 0, 15825, 'Brann Bronzebeard - SAY_BRANN_ALGALON_INTRO_2');
INSERT INTO `creature_text` VALUES (34064, 2, 0, 'I know just the place. Will you be all right?', 14, 0, 0, 6, 0, 15823, 'Brann Bronzebeard - SAY_BRANN_ALGALON_OUTRO');
INSERT INTO `creature_text` VALUES (32871, 0, 0, 'Translocation complete. Commencing planetary analysis of Azeroth.', 12, 0, 0, 0, 0, 15405, 'Algalon the Observer - SAY_ALGALON_INTRO_1');
INSERT INTO `creature_text` VALUES (32871, 1, 0, 'Stand back, mortals. I\'m not here to fight you.', 12, 0, 0, 0, 0, 15406, 'Algalon the Observer - SAY_ALGALON_INTRO_2');
INSERT INTO `creature_text` VALUES (32871, 2, 0, 'It is in the universe\'s best interest to re-originate this planet should my analysis find systemic corruption. Do not interfere.', 12, 0, 0, 0, 0, 15407, 'Algalon the Observer - SAY_ALGALON_INTRO_3');
INSERT INTO `creature_text` VALUES (32871, 3, 0, 'Your actions are illogical. All possible results for this encounter have been calculated. The Pantheon will receive the Observer\'s message regardless of outcome.', 14, 0, 0, 0, 0, 15386, 'Algalon the Observer - SAY_ALGALON_START_TIMER');
INSERT INTO `creature_text` VALUES (32871, 4, 0, 'See your world through my eyes: A universe so vast as to be immeasurable - incomprehensible even to your greatest minds.', 14, 0, 0, 1, 0, 15390, 'Algalon the Observer - SAY_ALGALON_AGGRO');
INSERT INTO `creature_text` VALUES (32871, 5, 0, 'The stars come to my aid!', 14, 0, 0, 0, 0, 15392, 'Algalon the Observer - SAY_ALGALON_COLLAPSING_STAR');
INSERT INTO `creature_text` VALUES (32871, 6, 0, '%s begins to Summon Collapsing Stars!', 41, 0, 0, 0, 0, 0, 'Algalon the Observer - EMOTE_ALGALON_COLLAPSING_STAR');
INSERT INTO `creature_text` VALUES (32871, 7, 0, 'Witness the fury of the cosmos!', 14, 0, 0, 0, 0, 15396, 'Algalon the Observer - SAY_ALGALONG_BIG_BANG');
INSERT INTO `creature_text` VALUES (32871, 8, 0, '%s begins to cast Big Bang!', 41, 0, 0, 0, 0, 0, 'Algalon the Observer - EMOTE_ALGALON_BIG_BANG');
INSERT INTO `creature_text` VALUES (32871, 9, 0, 'You are out of time.', 14, 0, 0, 0, 0, 15394, 'Algalon the Observer - SAY_ALGALON_ASCEND');
INSERT INTO `creature_text` VALUES (32871, 10, 0, '%s begins to cast Cosmic Smash!', 41, 0, 0, 0, 0, 0, 'Algalon the Observer - EMOTE_ALGALON_COSMIC_SMASH');
INSERT INTO `creature_text` VALUES (32871, 11, 0, 'Behold the tools of creation!', 14, 0, 0, 0, 0, 15397, 'Algalon the Observer - SAY_ALGALON_PHASE_TWO');
INSERT INTO `creature_text` VALUES (32871, 12, 0, 'I have seen worlds bathed in the Makers\' flames, their denizens fading without as much as a whimper. Entire planetary systems born and razed in the time that it takes your mortal hearts to beat once. Yet all throughout, my own heart devoid of emotion... of empathy. I. Have. Felt. Nothing. A million-million lives wasted. Had they all held within them your tenacity? Had they all loved life as you do?', 14, 0, 0, 1, 0, 15393, 'Algalon the Observer - SAY_ALGALON_OUTRO_1');
INSERT INTO `creature_text` VALUES (32871, 13, 0, 'Perhaps it is your imperfections... that which grants you free will... that allows you to persevere against all cosmically calculated odds. You prevail where the Titan\'s own perfect creations have failed.', 14, 0, 0, 1, 0, 15401, 'Algalon the Observer - SAY_ALGALON_OUTRO_2');
INSERT INTO `creature_text` VALUES (32871, 14, 0, 'I\'ve rearranged the reply code - your planet will be spared. I cannot be certain of my own calculations anymore.', 14, 0, 0, 1, 0, 15402, 'Algalon the Observer - SAY_ALGALON_OUTRO_3');
INSERT INTO `creature_text` VALUES (32871, 15, 0, 'I lack the strength to transmit the signal. You must... hurry... find a place of power... close to the skies.', 14, 0, 0, 1, 0, 15403, 'Algalon the Observer - SAY_ALGALON_OUTRO_4');
INSERT INTO `creature_text` VALUES (32871, 16, 0, 'Do not worry about my fate, Bronzen. If the signal is not transmitted in time, re-origination will proceed regardless. Save... your world...', 14, 0, 0, 1, 0, 15404, 'Algalon the Observer - SAY_ALGALON_OUTRO_5');
INSERT INTO `creature_text` VALUES (32871, 17, 0, 'Analysis complete. There is partial corruption in the planet\'s life-support systems as well as complete corruption in most of the planet\'s defense mechanisms.', 12, 0, 0, 0, 0, 15398, 'Algalon the Observer - SAY_ALGALON_DESPAWN_1');
INSERT INTO `creature_text` VALUES (32871, 18, 0, 'Begin uplink: Reply Code: \'Omega\'. Planetary re-origination requested.', 12, 0, 0, 0, 0, 15399, 'Algalon the Observer - SAY_ALGALON_DESPAWN_2');
INSERT INTO `creature_text` VALUES (32871, 19, 0, 'Farewell, mortals. Your bravery is admirable, for such flawed creatures.', 12, 0, 0, 0, 0, 15400, 'Algalon the Observer - SAY_ALGALON_DESPAWN_3');
INSERT INTO `creature_text` VALUES (32871, 20, 0, 'Loss of life unavoidable.', 14, 0, 0, 0, 0, 15387, 'Algalon the Observer - SAY_ALGALON_KILL');
INSERT INTO `creature_text` VALUES (32871, 20, 1, 'I do what I must.', 14, 0, 0, 0, 0, 15388, 'Algalon the Observer - SAY_ALGALON_KILL');
INSERT INTO `creature_text` VALUES (37813, 14, 0, '%s\'s Blood Beasts gain the scent of blood!', 41, 0, 0, 0, 0, 0, 'Deathbringer Saurfang - EMOTE_SCENT_OF_BLOOD');
INSERT INTO `creature_text` VALUES (16577, 0, 0, 'Brothers and sisters, I have been to the promised land. I have tasted in the sublim energy. I have felt bliss - bliss so engrossing and all encompassing that I was left wondering if I had stumbled upon the dreams of gods.!', 12, 0, 100, 5, 0, 0, 'Martik Tor\'seldoi');
INSERT INTO `creature_text` VALUES (16577, 1, 0, 'In paradise, you merely reach out and take from the Nether.', 12, 0, 100, 1, 0, 0, 'Martik Tor\'seldoi');
INSERT INTO `creature_text` VALUES (16577, 2, 0, 'From the very air! Tendrils of arcane energy light up the obsidian sky as plumes of raw magic rise up from fissures in the land. Kneel and drink from the fissure as you do from a stream or well. Pluck a tendril from the heavens as if it were an apple hanging from a branch.', 12, 0, 100, 5, 0, 0, 'Martik Tor\'seldoi');
INSERT INTO `creature_text` VALUES (16578, 0, 0, 'Tell us more, Martik. What is it? What does it feel like?', 12, 0, 100, 1, 0, 0, 'Blood Elf Pilgrim');
INSERT INTO `creature_text` VALUES (16578, 1, 0, 'But how?', 12, 0, 100, 6, 0, 0, 'Blood Elf Pilgrim');
INSERT INTO `creature_text` VALUES (16578, 2, 0, '%s smiles/gasps.', 16, 0, 100, 0, 0, 0, 'Blood Elf Pilgrim');
INSERT INTO `creature_text` VALUES (36478, 0, 0, '%s goes into a frenzy!', 16, 0, 100, 0, 0, 0, 'Soulguard Watchman');
INSERT INTO `creature_text` VALUES (17226, 0, 0, 'Ok, let\'s go. I know just the perfect spot!', 12, 0, 100, 0, 0, 0, 'Viera Sunwhisper');
INSERT INTO `creature_text` VALUES (17226, 1, 0, 'I can\'t wait to try this wine!', 12, 0, 100, 0, 0, 0, 'Viera Sunwhisper');
INSERT INTO `creature_text` VALUES (17226, 2, 0, 'What... is happening... to me? Get this cat away from me!', 12, 0, 100, 0, 0, 0, 'Viera Sunwhisper');
INSERT INTO `creature_text` VALUES (17226, 3, 0, '%s scurries away, attempting to flee from Twinkle', 16, 0, 100, 0, 0, 0, 'Viera Sunwhisper');
INSERT INTO `creature_text` VALUES (17230, 0, 0, '%s looks at Viera Sunwhisper with very hungry eyes', 16, 0, 100, 0, 0, 0, 'Twinkle');
