INSERT INTO `creature_text` VALUES (36764, 0, 1, '%s lets his mining pick drag on the ground as she approaches, clearly exhausted.', 16, 0, 100, 0, 0, 0, 'Alliance Slave');
INSERT INTO `creature_text` VALUES (36764, 0, 2, 'Have my babies.', 12, 0, 100, 0, 0, 0, 'Alliance Slave');
INSERT INTO `creature_text` VALUES (36764, 0, 3, 'If by life or death I can repay you, I will.', 12, 0, 100, 0, 0, 0, 'Alliance Slave');
INSERT INTO `creature_text` VALUES (36764, 0, 4, 'I\'m going to return to the front of the quarry. Kill a few extra for me.', 12, 0, 100, 0, 0, 0, 'Alliance Slave');
