INSERT INTO `creature_text` VALUES (26902, 0, 0, 'How dare you summon me without an offering!', 42, 0, 0, 0, 0, 0, 'Essence of Warlord Jin\'arrak');
INSERT INTO `creature_text` VALUES (36494, 0, 0, 'Tiny creatures under feet, you bring Garfrost something good to eat!', 14, 0, 100, 0, 0, 0, 'Forgemaster Garfrost');
INSERT INTO `creature_text` VALUES (36494, 1, 0, 'Axe too weak. Garfrost make better and CRUSH YOU.', 14, 0, 100, 0, 0, 0, 'Forgemaster Garfrost');
INSERT INTO `creature_text` VALUES (36494, 2, 0, 'Garfrost tired of puny mortals. Now your bones will freeze!', 14, 0, 100, 0, 0, 0, 'Forgemaster Garfrost');
INSERT INTO `creature_text` VALUES (36494, 3, 0, 'Garfrost hope giant underpants clean. Save boss great shame. For later.', 14, 0, 100, 0, 0, 0, 'Forgemaster Garfrost');
INSERT INTO `creature_text` VALUES (36494, 4, 0, 'Will save for snack. For later.', 12, 0, 100, 0, 0, 0, 'Forgemaster Garfrost');
INSERT INTO `creature_text` VALUES (36494, 4, 1, 'That one maybe not so good to eat now. Stupid Garfrost! BAD! BAD!', 12, 0, 100, 0, 0, 0, 'Forgemaster Garfrost');
INSERT INTO `creature_text` VALUES (36494, 5, 0, '%s hurls a massive saronite boulder at you!', 16, 0, 100, 0, 0, 0, 'Forgemaster Garfrost');
INSERT INTO `creature_text` VALUES (36494, 6, 0, '%s casts |cFF00AACCDeep Freeze|r at $n.', 41, 0, 100, 0, 0, 0, 'Forgemaster Garfrost');
INSERT INTO `creature_text` VALUES (33134, 0, 0, 'Help me! Please get them off me!', 14, 0, 100, 0, 0, 15771, 'Sara YELL_PREFIGHT');
INSERT INTO `creature_text` VALUES (33134, 0, 1, 'What do you want from me? Leave me alone!', 14, 0, 100, 0, 0, 15772, 'Sara YELL_PREFIGHT');
INSERT INTO `creature_text` VALUES (33134, 1, 0, 'Yes! YES! Show them no mercy! Give no pause to your attacks!', 14, 0, 100, 0, 0, 15773, 'Sara YELL_COMBAT_PHASE_1');
INSERT INTO `creature_text` VALUES (33134, 1, 1, 'Let hatred and rage guide your blows!', 14, 0, 100, 0, 0, 15774, 'Sara YELL_COMBAT_PHASE_1');
INSERT INTO `creature_text` VALUES (33134, 1, 2, 'The time to strike at the head of the beast will soon be upon us! Focus your anger and hatred on his minions!', 14, 0, 100, 457, 0, 15775, 'Sara YELL_COMBAT_PHASE_1');
INSERT INTO `creature_text` VALUES (33134, 2, 0, 'Suffocate upon your own hate!', 14, 0, 100, 0, 0, 15776, 'Sara YELL_COMBAT_PHASE_2');
INSERT INTO `creature_text` VALUES (33134, 2, 1, 'Tremble, mortals, before the coming of the end!', 14, 0, 100, 0, 0, 15777, 'Sara YELL_COMBAT_PHASE_2');
INSERT INTO `creature_text` VALUES (33134, 3, 0, 'Powerless to act...', 14, 0, 100, 0, 0, 15778, 'Sara YELL_SLAY');
INSERT INTO `creature_text` VALUES (33134, 3, 1, 'Could they have been saved?', 14, 0, 100, 0, 0, 15779, 'YELL SAY_SLAY');
INSERT INTO `creature_text` VALUES (9816, 0, 0, '%s begins to regain its strength!', 16, 0, 100, 0, 0, 0, 'Pyroguard Emberseer');
INSERT INTO `creature_text` VALUES (9816, 1, 0, '%s is nearly at full strength!', 16, 0, 100, 0, 0, 0, 'Pyroguard Emberseer');
INSERT INTO `creature_text` VALUES (9816, 2, 0, '%s regains its power and breaks free of its bonds!', 16, 0, 100, 0, 0, 0, 'Pyroguard Emberseer');
INSERT INTO `creature_text` VALUES (9816, 3, 0, 'Ha! Ha! Ha! Thank you for freeing me, fools. Now let me repay you by charring the flesh from your bones.', 14, 0, 100, 0, 0, 0, 'Pyroguard Emberseer');
INSERT INTO `creature_text` VALUES (9818, 0, 0, '%s begins to summon in a Blackhand Veteran!', 16, 0, 100, 0, 0, 0, 'Blackhand Summoner');
INSERT INTO `creature_text` VALUES (9818, 1, 0, '%s begins to summon in a Blackhand Dreadweaver!', 16, 0, 100, 0, 0, 0, 'Blackhand Summoner');
INSERT INTO `creature_text` VALUES (9096, 0, 0, '%s goes into a frenzy!', 16, 0, 100, 0, 0, 0, 'Rage Talon Dragonspawn');
INSERT INTO `creature_text` VALUES (4983, 0, 0, 'I noticed some fire on that island over there. A human, too. Let\'s go check it out, $N.', 12, 0, 0, 0, 0, 0, 'Ogron');
INSERT INTO `creature_text` VALUES (4983, 1, 0, 'That\'s Reethe alright. Let\'s go see what he has to say, yeah?', 12, 0, 0, 0, 0, 0, 'Ogron');
INSERT INTO `creature_text` VALUES (4980, 0, 0, 'W-what do you want? Just leave me alone...', 12, 0, 0, 0, 0, 0, 'Paval');
INSERT INTO `creature_text` VALUES (4980, 1, 0, 'I swear, I didn\'t steal anything from you! Here, take some of my supplies, just go away!', 12, 0, 0, 0, 0, 0, 'Paval');
INSERT INTO `creature_text` VALUES (4983, 2, 0, 'Just tell us what you know about the Shady Rest Inn, and I won\'t bash your skull in.', 12, 0, 0, 0, 0, 0, 'Ogron');
INSERT INTO `creature_text` VALUES (4980, 2, 0, 'I... Well, I may of taken a little thing or two from the inn... but what would an ogre care about that?', 12, 0, 0, 0, 0, 0, 'Paval');
INSERT INTO `creature_text` VALUES (4983, 3, 0, 'Look here, if you don\'t tell me about the fire--', 12, 0, 0, 0, 0, 0, 'Ogron');
INSERT INTO `creature_text` VALUES (4980, 3, 0, 'Not one step closer, ogre!', 12, 0, 0, 0, 0, 0, 'Paval');
INSERT INTO `creature_text` VALUES (4980, 4, 0, 'And I don\'t know anything about this fire of yours...', 12, 0, 0, 0, 0, 0, 'Paval');
INSERT INTO `creature_text` VALUES (4980, 5, 0, 'What was that? Did you hear something?', 12, 0, 0, 0, 0, 0, 'Paval');
INSERT INTO `creature_text` VALUES (5046, 0, 0, 'Paval Reethe! Found you at last. And consorting with ogres now? No fear, even deserters and traitors are afforded some mercy.', 12, 0, 0, 0, 0, 0, 'Caldwell');
INSERT INTO `creature_text` VALUES (5046, 1, 0, 'Private, show Lieutenant Reethe some mercy.', 12, 0, 0, 0, 0, 0, 'Caldwell');
INSERT INTO `creature_text` VALUES (5045, 0, 0, 'Gladly, sir.', 12, 0, 0, 0, 0, 0, 'Hallan');
INSERT INTO `creature_text` VALUES (4980, 6, 0, '%s staggers back as the arrow lodges itself deeply in his chest.', 16, 0, 0, 0, 0, 0, 'Paval');
INSERT INTO `creature_text` VALUES (4980, 7, 0, 'Ugh... Hallan, didn\'t think you had it in you...', 12, 0, 0, 16, 0, 0, 'Paval');
INSERT INTO `creature_text` VALUES (5046, 2, 0, 'Now, let\'s clean up the rest of the trash, men!', 12, 0, 0, 0, 0, 0, 'Caldwell');
INSERT INTO `creature_text` VALUES (4983, 4, 0, 'Damn it! You\'d better not die on me, human!', 12, 0, 0, 0, 0, 0, 'Ogron');
INSERT INTO `creature_text` VALUES (4983, 5, 0, 'Still with us, Reethe?', 12, 0, 0, 0, 0, 0, 'Ogron');
INSERT INTO `creature_text` VALUES (4980, 8, 0, 'Must be your lucky day. Alright, I\'ll talk. Just leave me alone. Look, you\'re not going to believe me, but it wa... oh, Light, looks like the girl could shoot...', 12, 0, 0, 0, 0, 0, 'Paval');
INSERT INTO `creature_text` VALUES (4983, 6, 0, 'Looks dead to me. Not much use to us like this. You\'d better go back and tell Krog what happened.', 12, 0, 0, 0, 0, 0, 'Ogron');
INSERT INTO `creature_text` VALUES (4983, 7, 0, 'By the way, thanks for watching my back.', 12, 0, 0, 0, 0, 0, 'Ogron');
INSERT INTO `creature_text` VALUES (4983, 8, 0, 'Me smash! You die!', 12, 0, 0, 0, 0, 0, 'Ogron');
INSERT INTO `creature_text` VALUES (4983, 8, 1, 'I\'ll crush you!', 12, 0, 0, 0, 0, 0, 'Ogron');
INSERT INTO `creature_text` VALUES (28328, 0, 0, 'The only thing hurt is your pride, lad. Buck up!', 12, 0, 100, 11, 0, 0, 'Drostan to Lucky Wilhelm');
INSERT INTO `creature_text` VALUES (28328, 0, 1, 'Stop whining. You\'ve still got your luck.', 12, 0, 100, 11, 0, 0, 'Drostan to Lucky Wilhelm');
INSERT INTO `creature_text` VALUES (28328, 0, 2, 'Bah, it\'s an improvement.', 12, 0, 100, 0, 0, 0, 'Drostan to Lucky Wilhelm');
INSERT INTO `creature_text` VALUES (28328, 0, 3, 'Calm down lad, it\'s just a birdshot!', 12, 0, 100, 0, 0, 0, 'Drostan to Lucky Wilhelm');
INSERT INTO `creature_text` VALUES (28054, 0, 0, 'Not the \'stache! Now I\'m asymmetrical!', 12, 0, 100, 5, 0, 0, 'Lucky Wilhelm');
