INSERT INTO `creature_text` VALUES (27003, 0, 0, 'I know why you\'ve come - one of those foolish Magnataur on the plains meddled and managed to get the dragons involved. Do you enjoy serving them like a dog?', 14, 0, 100, 0, 0, 0, 'Dregmar Runebrand - yell');
INSERT INTO `creature_text` VALUES (27003, 1, 0, 'You seek their leader... little thing, you wage war against the clans of Grom\'thar the Thunderbringer himself. Don\'t be so eager to rush to your death.', 14, 0, 100, 0, 0, 0, 'Dregmar Runebrand yell');
INSERT INTO `creature_text` VALUES (27003, 2, 0, 'Hah! So be it. Blow the horn of a magnataur leader at the ring of torches south of the Azure Dragonshrine. Make peace with your gods... Grom\'thar will come.', 14, 0, 100, 0, 0, 0, 'Dregmar Runebrand yell');
INSERT INTO `creature_text` VALUES (27482, 0, 0, 'I\'d nearly given up.You\'ve given me new life!', 12, 0, 50, 0, 0, 0, 'Wounded Westfall Infantry say text');
INSERT INTO `creature_text` VALUES (27482, 0, 1, 'Bless you, friend.I nearly expired....', 12, 0, 50, 0, 0, 0, 'Wounded Westfall Infantry say text');
INSERT INTO `creature_text` VALUES (27482, 0, 2, 'Without your help, I surely would have died....', 12, 0, 50, 0, 0, 0, 'Wounded Westfall Infantry say text');
INSERT INTO `creature_text` VALUES (27482, 0, 3, 'Thank you $r.', 12, 0, 50, 0, 0, 0, 'Wounded Westfall Infantry say text');
INSERT INTO `creature_text` VALUES (28041, 0, 0, 'Careful here, $C. These trolls killed their own snake god!', 15, 0, 50, 0, 0, 0, 'Argent Soldier whisper text');
INSERT INTO `creature_text` VALUES (28041, 0, 1, 'Watch your back. These Drakkari are a nasty lot.', 15, 0, 50, 0, 0, 0, 'Argent Soldier whisper text');
INSERT INTO `creature_text` VALUES (28041, 0, 2, 'These Drakkari are just bad news. We need to leave and head back to Justice Keep!', 15, 0, 50, 0, 0, 0, 'Argent Soldier whisper text');
INSERT INTO `creature_text` VALUES (28041, 0, 3, 'See you around.', 15, 0, 50, 0, 0, 0, 'Argent Soldier whisper text');
INSERT INTO `creature_text` VALUES (28041, 0, 4, 'I wonder where we\'re headed to. And who\'s going to deal with these guys?', 15, 0, 50, 0, 0, 0, 'Argent Soldier whisper text');
INSERT INTO `creature_text` VALUES (28041, 0, 5, 'Right. I\'d better get back to the sergeant then.', 15, 0, 50, 0, 0, 0, 'Argent Soldier whisper text');
INSERT INTO `creature_text` VALUES (28041, 0, 6, 'Are you $N? I heard you were dead.', 15, 0, 50, 0, 0, 0, 'Argent Soldier whisper text');
