INSERT INTO `creature_text` VALUES (35458, 2, 0, 'A-HA! I\'ve done it! Behold the absolute power of Wilfred Fizzlebang, master summoner! You are bound to me, demon!', 14, 0, 100, 5, 0, 16270, 'Wilfred Fizzlebang to Wilfred Fizzlebang - Intro');
INSERT INTO `creature_text` VALUES (35458, 3, 0, 'But I\'m in charge here...', 14, 0, 100, 5, 0, 16271, 'Wilfred Fizzlebang to Wilfred Fizzlebang - Death');
